INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-phoenix-2029-water-crisis', 'Феникс 2020-2029 — Водный кризис пустыни', 'Игрок анализирует инфраструктуру Центрального канала Аризоны и принимает решение, как реагировать на кризис.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-phoenix-2029-water-crisis", "title": "Феникс 2020-2029 — Водный кризис пустыни", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["phoenix", "ecology", "water"], "topics": ["environmental_risk", "urban_planning"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/phoenix/2020-2029/quest-006-water-crisis.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экологическая линия Феникса не охватывала зависимость города от истощающихся ресурсов Колорадо.", "goal": "Исследовать угрозу высыхания озера Мид, конкуренцию штатов за воду и потенциальные исходы для мегаполиса.", "essence": "Игрок анализирует инфраструктуру Центрального канала Аризоны и принимает решение, как реагировать на кризис.", "key_points": ["Город получает меньше 20 см осадков в год и полностью зависит от реки Колорадо.", "Уровень озера Мид падает, что провоцирует конфликты между штатами-потребителями.", {"Судьба Феникса требует стратегических решений": "адаптация, миграция или инновации."}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока через основные узлы водной инфраструктуры Феникса, показывает масштабы дефицита и предлагает\\nоценить сценарии будущего мегаполиса в пустыне.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Изучить климатические данные и историю строительства водопроводных систем в пустыне.\\n2. Посетить Центральный канал Аризоны, зафиксировать потери воды и протяжённость трубопровода.\\n3. Проанализировать снижение уровня озера Мид и переговорные позиции штатов.\\n4. Выявить социальные и экономические риски для жителей Феникса.\\n5. Принять решение: инвестировать в новые технологии, организовать эвакуацию или игнорировать угрозу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Феникс — крупнейший город пустынной Аризоны; ежегодные осадки около 200 мм. Центральный канал Аризоны транспортирует\\nводу от Колорадо на сотни километров. Переговоры семи штатов и Мексики за долю Колорадо становятся всё напряжённее.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Атрибут «Экология»: +25\\n- Ачивка: «Свидетель кризиса»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует экологические и геополитические механики, влияя на доступ к ресурсам, отношения с соседними штатами\\nи устойчивость городской экономики.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с экологической линией Феникса, политическими сюжетами Колорадо и миссиями по устойчивому развитию.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1078, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Водный кризис пустыни» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

