-- Quest: quest-phoenix-2029-mexican-food
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-phoenix-2029-mexican-food', 'Феникс 2020-2029 — Сонорская кухня', 'Игрок посещает мексиканские районы, сравнивает рецепты и знакомится с историей семейных заведений.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-phoenix-2029-mexican-food", "title": "Феникс 2020-2029 — Сонорская кухня", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["phoenix", "cuisine", "sonoran"], "topics": ["culinary_traditions", "cultural_identity"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/phoenix/2020-2029/quest-007-mexican-food.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия Феникса не раскрывала влияние близости к Мексике и сонорской традиции на городскую кухню.", "goal": "Погрузить игрока в гастрономическую культуру латиноамериканского сообщества и показать знаковые блюда региона.", "essence": "Игрок посещает мексиканские районы, сравнивает рецепты и знакомится с историей семейных заведений.", "key_points": ["Феникс расположен в 300 км от границы и более 30% населения составляют латиноамериканцы.", "Сонорская кухня отличается сладковатым тестом и крупными порциями carne asada.", "Местные пекарни и taqueria сохраняют связи с родными регионами Мексики."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока по мексиканским кварталам Феникса, знакомит с традиционными блюдами и рассказами владельцев\\nсемейных ресторанов, формируя кулинарную карту города.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить сонорский район и изучить меню популярных taqueria.\\n2. Продегустировать carne asada burrito, tacos и напиток horchata, оценить вариации рецептов.\\n3. Собрать истории семейных бизнесов и их связи с пограничными городами Мексики.\\n4. Исследовать влияние миграции на ассортимент и открытие новых заведений.\\n5. Подготовить гастрономический путеводитель, отмечая острые соусы и локальные ингредиенты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Феникс сформирован миграцией из штата Сонора. Блюда отличаются большим количеством мяса на гриле, мучными изделиями\\nи сладкими напитками. Уличные рынки предлагают свежие ингредиенты, поставляемые через пограничные города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 800 XP\\n- Расходы: 15 единиц валюты\\n- Бафф здоровья: +20% HP на 6 часов\\n- Ачивка: «Сонорский гурман»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует кулинарные и социальные механики, повышает репутацию среди латиноамериканских фракций и открывает\\nдоступ к гастрономическим событиям.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с кулинарной линией Феникса, заданиями о пограничной культуре и миссиями устойчивого развития городских рынков.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1079, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Сонорская кухня» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

