-- Quest: canon-quest-tokyo-zen-garden
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-zen-garden', 'Токио 2061-2077 — «Дзен-сад Ryoanji»', 'Игрок отправляется в храм Ryoanji, проходит медитацию в саду камней и получает бафф «Сатори».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-zen-garden", "title": "Токио 2061-2077 — «Дзен-сад Ryoanji»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T23:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["tokyo", "kyoto", "meditation"], "topics": ["spiritual-content", "buffs", "cultural-experience"], "related_systems": ["narrative-service", "buff-system", "stress-system"], "related_documents": [{"id": "canon-quest-tokyo-spiritual-line", "relation": "belongs_to"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-034-zen-garden.md", "visibility": "internal", "audience": ["lore", "gameplay", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спокойный квест-медитация находился в виде заметки и не был описан в системе баффов и культурных событий.", "goal": "Зафиксировать медитативную активность, связывающую Токио и Киото, с чёткими шагами и эффектами.", "essence": "Игрок отправляется в храм Ryoanji, проходит медитацию в саду камней и получает бафф «Сатори».", "key_points": ["Квест подчёркивает дзен-буддизм и позволяет временно снизить стресс персонажа.", "Игрок жертвует храму, чтобы получить доступ к мини-играм медитации.", "Эффекты распространяются на боевые и социальные проверки через бафф Focus."]}, "content": {"sections": [{"id": "travel", "title": "Путешествие и антураж", "body": "Игрок едет из Токио в Киото, чтобы посетить храм Ryoanji. Локация включает традиционный сад камней из 15 камней,\\nбелый гравий с волнообразными узорами и спокойствие, нарушаемое лишь звуками природы и приглушёнными шагами монахов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы опыта", "body": "1. **Подготовка:** покупка билета, прибытие и короткая лекция монаха.\\n2. **Вход в сад:** игрок наблюдает за композицией, где всегда видны только 14 камней.\\n3. **Медитация:** мини-игра очищения сознания; успешные действия повышают шкалу Focus.\\n4. **Созерцание:** философские диалоги, раскрывающие личные конфликты персонажа.\\n5. **Сатори:** получение временного просветления и возврат к работе в городе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- **Опыт:** 1 000 XP.\\n- **Финансы:** требуемое пожертвование храму — 500 еддисов.\\n- **Бафф «Сатори»:** +20 % к Focus и -50 % накопления стресса на 48 игровых часов.\\n- **Достижение «Дзен-Мастер»:** открывает косметические предметы и доступ к продвинутым медитациям.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые особенности", "body": "- **Медитационная мини-игра:** использует дыхательные ритмы и реакции на окружающие стимулы.\\n- **Диалоги о философии:** выборы не дают материальных наград, но формируют ветки отношений с духовными наставниками.\\n- **Социальные последствия:** повышает доверие духовных фракций и снижает агрессию NPC в Киото.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Сатори", "definition": "Термин дзен-буддизма, обозначающий внезапное просветление."}, {"term": "Карэсансуй", "definition": "Сухой сад камней, символизирующий море и острова."}], "references": [{"title": "Ryoanji Temple Official Site", "link": "https://www.ryoanji.jp/"}], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "lore_team", "changes": "Конверсия квеста «Дзен-сад Ryoanji» в YAML и описание баффа «Сатори»."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

