INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-phoenix-2029-sedona-red-rocks', 'Феникс 2020-2029 — Красные скалы Седоны', 'Игрок преодолевает путь, исследует Cathedral Rock и Bell Rock, решая, верить ли в энергетические вихри.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-phoenix-2029-sedona-red-rocks", "title": "Феникс 2020-2029 — Красные скалы Седоны", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["phoenix", "sedona", "nature"], "topics": ["outdoor_activity", "spirituality"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/phoenix/2020-2029/quest-008-sedona-red-rocks.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природно-духовная линия Аризоны не показывала уникальность ландшафтов Седоны и феномена «вихрей».", "goal": "Организовать поездку из Феникса в Седону, чтобы исследовать красные скалы и духовные практики New Age.", "essence": "Игрок преодолевает путь, исследует Cathedral Rock и Bell Rock, решая, верить ли в энергетические вихри.", "key_points": ["Седона расположена в 180 км от Феникса и славится ярко-красными формациями.", "Локации Cathedral Rock и Bell Rock считаются точками «вихрей» для последователей New Age.", "Место объединяет outdoor-туризм, духовные практики и арт-сообщество."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку совершить поездку в Седону, исследовать природные памятники и познакомиться с духовными\\nтрадициями города, чтобы расширить карту природных активностей Феникса.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Седоны по трассе I-17 и посетить туристический центр.\\n2. Совершить хайкинг к Cathedral Rock и Bell Rock, собрать панорамные данные.\\n3. Изучить точки «вихрей» и провести интервью с практиками New Age.\\n4. Оценить инфраструктуру для outdoor-туризма и арт-галереи города.\\n5. Сделать вывод: использовать локацию как духовный курорт или просто природный аттракцион.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Красные песчаниковые формации Седоны сформированы миллионы лет назад. Город позиционируется как духовный центр\\nс 1980-х, благодаря популярности New Age. Климат позволяет активный туризм круглый год.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP\\n- Расходы: 40 единиц валюты\\n- Атрибут «Духовность»: +20\\n- Ачивка: «Искатель вихрей»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает природные и духовные механики, даёт бонусы к выносливости в походах и открывает доступ к духовным\\nсобытиям Аризоны.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с природной и духовной линиями Аризоны, сюжетами о туризме Соноры и миссиями культурного взаимодействия.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1080, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Красные скалы Седоны» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

