INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-phoenix-2029-spring-training', 'Феникс 2020-2029 — Весенние тренировки MLB', 'Игрок посещает тренировки Cactus League, общается с болельщиками и собирает эксклюзивные автографы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-phoenix-2029-spring-training", "title": "Феникс 2020-2029 — Весенние тренировки MLB", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["phoenix", "baseball", "sports"], "topics": ["sports_culture", "seasonal_events"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/phoenix/2020-2029/quest-009-spring-training.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивная линия Феникса не демонстрировала значение лиги Cactus и весенних тренировок MLB.", "goal": "Показать атмосферу подготовки команд в феврале-марте, близость фанатов к игрокам и туристическую выгоду региона.", "essence": "Игрок посещает тренировки Cactus League, общается с болельщиками и собирает эксклюзивные автографы.", "key_points": ["В Аризоне тренируется 15 команд MLB в рамках Cactus League.", "Весенние матчи проходят на небольших стадионах с расслабленной атмосферой.", "Событие привлекает туристов и формирует сезонные доходы города."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока на весенние тренировки MLB в пригородах Феникса. Нужно посетить матч, изучить логистику\\nкоманд и оценить взаимодействие фанатов с игроками.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в Феникс в период февраля-марта и выбрать стадион Cactus League.\\n2. Посетить тренировку, собрать материалы о расписании и составе команды.\\n3. Поучаствовать в фан-сессии, получить автографы и фото с игроками.\\n4. Проанализировать экономический эффект для местных бизнесов.\\n5. Подготовить отчёт о роли тёплого климата и инфраструктуры Аризоны.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Cactus League объединяет стадионы в Глендейле, Мессе и других пригородах. Тёплая погода и компактные площадки\\nсоздают неформальную атмосферу. Туристы бронируют отели заранее, а местные рестораны устраивают тематические акции.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 200 XP\\n- Расходы: 25 единиц валюты\\n- Атрибут «Спорт»: +15\\n- Ачивка: «Spring Training фанат»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует спортивные и социальные механики, повышает репутацию среди бейсбольных фракций и открывает\\nдополнительные мероприятия MLB.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан со спортивной линией Феникса, заданиями Cactus League и сценариями туристической инфраструктуры Аризоны.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1081, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Весенние тренировки MLB» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

