-- Quest: canon-quest-phoenix-2020-2029-urban-sprawl
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-phoenix-2020-2029-urban-sprawl', 'Феникс 2020-2029 — «Городская экспансия»', 'Игрок исследует расползающиеся пригороды Феникса, анализирует зависимость от автомобилей и риски нехватки воды.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-phoenix-2020-2029-urban-sprawl", "title": "Феникс 2020-2029 — «Городская экспансия»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["phoenix", "quest", "urbanism", "environment"], "topics": ["city_quests", "infrastructure"], "related_systems": ["narrative-service", "world-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/phoenix/2020-2029/quest-010-urban-sprawl.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Урбанистическая тема Феникса отражена поверхностно и не раскрывает последствия экстенсивного роста в пустыне.", "goal": "Погрузить игрока в исследование urban sprawl, показать инфраструктурные вызовы и дефицит ресурсов.", "essence": "Игрок исследует расползающиеся пригороды Феникса, анализирует зависимость от автомобилей и риски нехватки воды.", "key_points": ["Наблюдение за городом с высоты, демонстрирующее бесконечное расширение вдоль шоссе.", "Задания в пригородах, подчёркивающие однообразие застройки и автозависимость.", "Акцент на устойчивости и угрозе исчерпания водных ресурсов."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок прибывает в Феникс и получает задание городских планировщиков — изучить влияние стремительного пригорода на\\nэкологию и образ жизни жителей.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "observation", "title": "Наблюдение", "body": "Геликоптерный или дрон-тур демонстрирует, как кварталы растут в пустыне, рассечённые огромными шоссе. Игрок фиксирует\\nключевые точки расползания и собирает визуальные данные.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "suburban_life", "title": "Жизнь в пригороде", "body": "Игрок посещает типовой пригород: однообразные кварталы, отсутствие пешеходной инфраструктуры, зависимость от автомобилей\\nдля любых перемещений.\\n", "mechanics_links": ["mechanics/social/social-dialogue.yaml"], "assets": []}, {"id": "sustainability", "title": "Устойчивость", "body": "Анализируются проблемы водоснабжения, энергозатрат на охлаждение и экологический след. Игрок взаимодействует с экспертами\\nи активистами, предлагая решения.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 2 000, Валюта: 0.\\n- Показатель урбанистики +20 и ачивка «Свидетель экспансии».\\n- Доступ к дополнительным миссиям по устойчивому развитию в регионе.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1082, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Городская экспансия» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

