INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-sao-paulo-2020-2029-mega-city', 'Сан-Паулу 2020-2029 — «Мегаполис»', 'Игрок поднимается на смотровые площадки, исследует Avenida Paulista и наблюдает разрыв между небоскрёбами и фавелами.', 'main', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_observation_deck", "text": "Подняться на смотровую площадку Edifício Itália (46 этажей) и познакомиться с горизонтом", "type": "explore", "target": "sao_paulo_italia_building", "count": 1, "optional": false}, {"id": "explore_avenida_paulista", "text": "Перейти на Avenida Paulista — сердце финансового центра, посетить деловой квартал и проанализировать транспортную инфраструктуру", "type": "explore", "target": "sao_paulo_avenida_paulista", "count": 1, "optional": false}, {"id": "observe_contrasts", "text": "Наблюдать за контрастом между небоскрёбами и ближайшими жилыми массивами, фиксировать шум, рекламные голограммы и климатические особенности", "type": "observe", "target": "sao_paulo_urban_contrasts", "count": 1, "optional": false}, {"id": "interview_residents", "text": "Интервьюировать жителей о жизни в мегаполисе, собрать данные для урбанистической линии", "type": "social", "target": "sao_paulo_residents_interviews", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "currency": 0, "items": [], "reputation": {"sao_paulo_urbanism": 25}, "flags": ["megacity_resident_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-sao-paulo-2020-2029-mega-city", "title": "Сан-Паулу 2020-2029 — «Мегаполис»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["sao_paulo", "quest", "megacity"], "topics": ["urbanism", "city-scale"], "related_systems": ["narrative-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/sao-paulo/2020-2029/quest-001-mega-city.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по мегаполису Сан-Паулу находился в Markdown и не участвовал в урбанистической линии региона.", "goal": "Оформить опыт посещения основных локаций мегаполиса с акцентом на масштаб и контрасты.", "essence": "Игрок поднимается на смотровые площадки, исследует Avenida Paulista и наблюдает разрыв между небоскрёбами и фавелами.", "key_points": ["Сан-Паулу — крупнейший город Южного полушария с населением 22+ миллионов.", "Квест подчёркивает бесконечный горизонт небоскрёбов и легендарные пробки.", "Контрасты между финансовыми кварталами и соседними фавелами задают социальный контекст."]}, "quest_definition": {"quest_type": "main", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_observation_deck", "text": "Подняться на смотровую площадку Edifício Itália (46 этажей) и познакомиться с горизонтом", "type": "explore", "target": "sao_paulo_italia_building", "count": 1, "optional": false}, {"id": "explore_avenida_paulista", "text": "Перейти на Avenida Paulista — сердце финансового центра, посетить деловой квартал и проанализировать транспортную инфраструктуру", "type": "explore", "target": "sao_paulo_avenida_paulista", "count": 1, "optional": false}, {"id": "observe_contrasts", "text": "Наблюдать за контрастом между небоскрёбами и ближайшими жилыми массивами, фиксировать шум, рекламные голограммы и климатические особенности", "type": "observe", "target": "sao_paulo_urban_contrasts", "count": 1, "optional": false}, {"id": "interview_residents", "text": "Интервьюировать жителей о жизни в мегаполисе, собрать данные для урбанистической линии", "type": "social", "target": "sao_paulo_residents_interviews", "count": 1, "optional": false}], "rewards": {"experience": 2500, "currency": 0, "items": [], "reputation": {"sao_paulo_urbanism": 25}, "flags": ["megacity_resident_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с масштабом Сан-Паулу: панорамы, деловые центры и краски бесконечного мегаполиса. Игрок\\nсобирает впечатления о городе через наблюдения, интервью и навигацию по плотным кварталам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "route", "title": "Маршрут", "body": "1. Смотровая площадка Edifício Itália (46 этажей) — знакомство с горизонтом.\\n2. Переход на Avenida Paulista — сердце финансового центра.\\n3. Посещение делового квартала и анализ транспортной инфраструктуры.\\n4. Наблюдение за контрастом между небоскрёбами и ближайшими жилыми массивами.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "immersion", "title": "Погружение", "body": "Игрок фиксирует шум, рекламные голограммы, климатические особенности (город моросящего дождя) и интервьюирует\\nжителей о жизни в мегаполисе. Собранные данные влияют на репутацию в урбанистической линии.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описываются статистические факты: 22 миллиона жителей, статус экономической столицы Бразилии, бесконечные пробки,\\nиспользование вертолётов и климатические особенности (Cidade da Garoa).\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 500 XP, урбанистика +25.\\n- Ачивка «Житель Мегаполиса» и разблокировка последующих квестов о транспортной системе и фавелах.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Сбор урбанистических «отметок» для альбома города.\\n- Анализ трафика и плотности населения.\\n- Визуализация экономических данных и влияние игрока на городские индексы.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает социальную линию Сан-Паулу (фавелы и контрасты) и транспортную цепочку о городских пробках.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1071, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики урбанизма и масштаба города."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Мегаполис» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

