INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-samurai-duel', 'Токио 2040-2060 — Самурайская Дуэль', 'Игрок принимает вызов мастера кендо, готовится к бою и решает исход одним ударом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-samurai-duel", "title": "Токио 2040-2060 — Самурайская Дуэль", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "samurai", "duels"], "topics": ["honor", "high-risk-combat"], "related_systems": ["narrative-service", "duel-system", "reputation-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-023-samurai-duel.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Дуэль в Markdown не фиксировала quick-time механику и последствия для чести.", "goal": "Оформить квест в YAML, подчеркнув подготовку, церемонию и единственный решающий удар.", "essence": "Игрок принимает вызов мастера кендо, готовится к бою и решает исход одним ударом.", "key_points": ["Квест-id TOKYO-2060-023, тип side, сложность hard, длительность 1–2 часа.", "Бой реализован как high-stakes QTE с риском смерти.", "Победа даёт репутацию самураев и сохраняет честь."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "В рассветном саду под цветущей сакурой два бойца следуют бусидо; тишину прерывает единственный удар катаны.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Получить вызов и выполнить подготовительные задания: медитация, заточка оружия.\\n2. Пройти церемонию уважения, соблюдая поклоны и традиционные реплики.\\n3. Выполнить QTE-дуэль: успешный удар обеспечивает победу, ошибка ведёт к смертельному ранению.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Победа приносит 2 000 XP, +10 репутации самураев и титул защитника чести; поражение ведёт к смерти персонажа с потерей чести.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Самурайская Дуэль» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

