INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-manga-artist', 'Токио 2030-2039 — Мангака', 'Игрок запускает свой сериал в Weekly Jump, балансируя концепт, рисовку и дедлайны, чтобы добиться хита.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-manga-artist", "title": "Токио 2030-2039 — Мангака", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:00:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "manga", "creative-career"], "topics": ["publishing", "stress-management"], "related_systems": ["narrative-service", "career-system", "economy-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2030-2039/quest-016-manga-artist.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание мангаки не фиксировало экономику изданий, стресс и игровые мини-игры создания глав.", "goal": "Структурировать карьеру мангаки в YAML, связать с системами дедлайнов, ассистентов и издательских рейтингов.", "essence": "Игрок запускает свой сериал в Weekly Jump, балансируя концепт, рисовку и дедлайны, чтобы добиться хита.", "key_points": ["Квест-id TOKYO-2039-016, тип social, сложность medium, длительность 2–4 часа.", "Игровые шаги включают выбор жанра, мини-игры рисования страниц и управление стрессом команды.", "Успех приносит пассивный доход и репутацию в медиа-среде Токио."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Студия мангаки в Синдзюку наполнена шумом сканеров, энергетику и горами сценариев; Weekly Jump требует свежие главы каждую неделю.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Договориться с редактором Weekly Jump и выбрать жанр будущей серии.\\n2. Собрать ассистентов, распределить задачи и нарисовать пилотную главу через мини-игру.\\n3. Поддерживать график выпусков, управляя стрессом, финансами и рейтингами читателей.\\n4. Вести переговоры об аниме-адаптации или решать кризис падения рейтинга.\\n", "mechanics_links": [], "assets": []}, {"id": "production_challenges", "title": "Производственные сложности", "body": "Игрок принимает решения по дедлайнам, ночным сменам и качеству страниц, рискуя здоровьем персонажа и лояльностью ассистентов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успешный релиз даёт 3 000 XP, пассивный доход до 50 000 едди в неделю и +10 к славе; провал приносит 1 000 XP и возможность перезапуска.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 262, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Мангака» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

