INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-league-ceremony', 'Токио 2078-2093 — Церемония Лиги', 'Игрок участвует в торжественной церемонии закрытия, завершая сезон.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-league-ceremony", "title": "Токио 2078-2093 — Церемония Лиги", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "ceremony", "closure"], "topics": ["traditions", "emotional-events"], "related_systems": ["narrative-service", "event-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2078-2093/quest-042-league-ceremony.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание церемонии не фиксировало сценический порядок, награды и эмоциональное влияние.", "goal": "Структурировать финальное событие лиги, подчеркнув костюмы, процессии и финальные поклоны.", "essence": "Игрок участвует в торжественной церемонии закрытия, завершая сезон.", "key_points": ["Квест-id TOKYO-2093-042, тип event, сложность easy, длительность 1–2 часа.", "Процессия к храму, традиционные танцы и финальный поклон.", "Награды — 2 000 XP и достижение «Свидетель Церемонии»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Белые кимоно участников, барабаны тайко и опадающая сакура создают атмосферу прощания.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Надеть кимоно и собраться у храма Мэйдзи.\\n2. Участвовать в процессии, слушать речи и наблюдать танец буто.\\n3. Совершить финальный поклон и получить благодарность организаторов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 2 000 XP, эмоциональное завершение лиги и достижение «Свидетель Церемонии».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 266, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Церемония Лиги» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

