INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-final-ramen', 'Токио 2078-2093 — Последний Рамен', 'Игрок возвращается в любимый ларёк, вспоминает путь и прощается с мастером.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-final-ramen", "title": "Токио 2078-2093 — Последний Рамен", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "ramen", "nostalgia"], "topics": ["emotional-scenes", "culinary"], "related_systems": ["narrative-service", "mood-system", "memory-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2078-2093/quest-044-final-ramen.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История «Последний Рамен» не выделяла эмоциональную сцену и ссылки на ранние квесты.", "goal": "Оформить квест в YAML, подчеркнув ностальгию, разговор с мастером и завершение линии.", "essence": "Игрок возвращается в любимый ларёк, вспоминает путь и прощается с мастером.", "key_points": ["Квест-id TOKYO-2093-044, тип social, сложность easy, длительность 30–60 минут.", "Включает кат-сцену с воспоминаниями и разговоры о прошлом.", "Награды — 1 000 XP, тратит 500 едди, получает достижение «Душа Рамена»."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Ларёк рамена освещён тёплым светом; мастер готовит финальную миску, вспоминая стартовые приключения игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Вернуться в ларёк, заказать последнюю миску и обсудить пройденный путь.\\n2. Насладиться кат-сценой медленного поедания рамена и воспоминаний.\\n3. Прощаться с мастером, получая ободряющий совет на будущие дороги.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 1 000 XP, тратит 500 едди, испытывает ностальгию и получает достижение «Душа Рамена».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 266, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Последний Рамен» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

