INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-sao-paulo-2020-2029-football-corinthians', 'Сан-Паулу 2020-2029 — «Матч Corinthians и torcida Fiel»', 'Игрок готовится к дерби, общается с фан-клубами и переживает матч вместе с 30 миллионами поклонников.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "meet_torcida_leaders", "text": "Встретиться с лидерами torcida Fiel и подготовиться к матчу (флаги, барабаны, хореография)", "type": "interact", "target": "torcida_fiel_leaders", "count": 1, "optional": false}, {"id": "attend_match", "text": "Занять место на трибуне, синхронизировать песни и поддерживающие кричалки", "type": "social", "target": "neo_quimica_arena", "count": 1, "optional": false}, {"id": "observe_key_moments", "text": "Наблюдать ключевые моменты встречи, фиксируя эмоции болельщиков и реакцию команды", "type": "observe", "target": "match_key_moments", "count": 1, "optional": false}, {"id": "participate_parade", "text": "Участвовать в послематчевом шествии, оценить общественную значимость клуба для рабочих районов", "type": "social", "target": "post_match_parade", "count": 1, "optional": false}, {"id": "prepare_report", "text": "Подготовить отчёт для спортивного архива, подчеркнув особенности фан-культуры", "type": "craft", "target": "fan_culture_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "currency": -40, "items": [], "reputation": {"sao_paulo_sports": 30}, "flags": ["fiel_fan_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-sao-paulo-2020-2029-football-corinthians", "title": "Сан-Паулу 2020-2029 — «Матч Corinthians и torcida Fiel»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T17:12:21+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["sao-paulo", "football", "quest"], "topics": ["sports", "community", "quests"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "liveops-service"], "related_documents": [{"id": "canon-lore-regions-america-2020-2093", "relation": "references"}, {"id": "github-issue-1068", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1068", "relation": "migrated_to", "migrated_at": "2025-11-23T02:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/sao-paulo/2020-2029/quest-008-football-corinthians.md", "visibility": "internal", "audience": ["concept", "narrative", "live-ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивная ветка Сан-Паулу не охватывала феномен фанатов Corinthians и рабочую идентичность клуба.", "goal": "Погрузить игрока в матч на Neo Química Arena, показать силу torcida Fiel и её влияние на мегаполис.", "essence": "Игрок готовится к дерби, общается с фан-клубами и переживает матч вместе с 30 миллионами поклонников.", "key_points": ["Corinthians — один из самых популярных клубов Бразилии с массовой фан-базой рабочего класса.", "Torcida Fiel задаёт ритм стадиону и определяет политико-социальный контекст матчей.", "Квест усиливает спортивный атрибут и включает моральные выборы во время дерби."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "meet_torcida_leaders", "text": "Встретиться с лидерами torcida Fiel и подготовиться к матчу (флаги, барабаны, хореография)", "type": "interact", "target": "torcida_fiel_leaders", "count": 1, "optional": false}, {"id": "attend_match", "text": "Занять место на трибуне, синхронизировать песни и поддерживающие кричалки", "type": "social", "target": "neo_quimica_arena", "count": 1, "optional": false}, {"id": "observe_key_moments", "text": "Наблюдать ключевые моменты встречи, фиксируя эмоции болельщиков и реакцию команды", "type": "observe", "target": "match_key_moments", "count": 1, "optional": false}, {"id": "participate_parade", "text": "Участвовать в послематчевом шествии, оценить общественную значимость клуба для рабочих районов", "type": "social", "target": "post_match_parade", "count": 1, "optional": false}, {"id": "prepare_report", "text": "Подготовить отчёт для спортивного архива, подчеркнув особенности фан-культуры", "type": "craft", "target": "fan_culture_report", "count": 1, "optional": false}], "rewards": {"experience": 2500, "currency": -40, "items": [], "reputation": {"sao_paulo_sports": 30}, "flags": ["fiel_fan_title"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Обзор квеста", "body": "Квест приглашает игрока на матч Corinthians. Нужно попасть на Neo Química Arena, познакомиться с фанатами и\\nзафиксировать эмоции трибун на протяжении всего матча.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "1. Встретиться с лидерами torcida Fiel и подготовиться к матчу (флаги, барабаны, хореография).\\n2. Занять место на трибуне, синхронизировать песни и поддерживающие кричалки.\\n3. Наблюдать ключевые моменты встречи, фиксируя эмоции болельщиков и реакцию команды.\\n4. Участвовать в послематчевом шествии, оценить общественную значимость клуба для рабочих районов.\\n5. Подготовить отчёт для спортивного архива, подчеркнув особенности фан-культуры.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Torcida Fiel насчитывает десятки миллионов фанатов и известна непрерывной поддержкой.\\n- Corinthians исторически связан с рабочим классом и социальными движениями Сан-Паулу.\\n- Neo Química Arena была построена к ЧМ-2014 и остаётся современной площадкой для крупных событий.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 500 XP.\\n- Финансовые расходы: -40.\\n- Спортивный атрибут +30.\\n- Достижение «Фанат Fiel».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Спортивные события с динамическими толпами и звуковой поддержкой.\\n- Социальные проверки при взаимодействии с фанатскими фракциями.\\n- Репутация среди спортивных сообществ Бразилии.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает футбольную линию Бразилии с сюжетами о рабочем классе и открывает миссии о дерби с Palmeiras и São Paulo FC.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1068, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики подготовки к матчу, участия в трибунах, наблюдения за игрой и послематчевого шествия."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Создан YAML-квест «Матч Corinthians и torcida Fiel» с акцентом на фанатскую культуру."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

