-- Quest: canon-quest-tokyo-bullet-train-heist
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-bullet-train-heist', 'Токио 2061-2077 — «Ограбление Синкансена»', 'Игроки вооружённой команды похищают корпоративные секреты из скоростного поезда, движущегося со скоростью 500 км/ч между Токио и Осакой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-bullet-train-heist", "title": "Токио 2061-2077 — «Ограбление Синкансена»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T23:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["tokyo", "shinkansen", "heist"], "topics": ["transportation", "covert-ops", "high-speed"], "related_systems": ["narrative-service", "transport-service", "loot-service"], "related_documents": [{"id": "canon-quest-tokyo-logistics-network", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-033-bullet-train-heist.md", "visibility": "internal", "audience": ["lore", "mission-design", "live-ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий ограбления синкансена хранился в Markdown-описании и не учитывался при планировании мировых событий.", "goal": "Перевести квест в формат knowledge-entry, чтобы использовать его в расписании рейдов, логистических событий и рейтинговых активностей.", "essence": "Игроки вооружённой команды похищают корпоративные секреты из скоростного поезда, движущегося со скоростью 500 км/ч между Токио и Осакой.", "key_points": ["Квест сочетает динамические сцены в движущейся локации и тайм-лимит.", "Центральная проверка — взлом грузового вагона и координация эвакуации на ховербайках.", "Миссия подчёркивает японскую пунктуальность и визуальные отсылки к маршруту Tokaido."]}, "content": {"sections": [{"id": "overview", "title": "Сценарий и антураж", "body": "Команда получает контракт на похищение контейнера с корпоративными секретами, перевозимыми синкансеном Tokaido.\\nМиссия разворачивается внутри поезда во время движения, что создаёт напряжение и требует точной координации. Вид на гору Фудзи,\\nпродавцы бенто и объявление расписаний погружают игроков в японскую культуру.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы операции", "body": "1. **Планирование:** анализ маршрута и составление графика потока охраны.\\n2. **Посадка:** проникновение на поезд в Токио и разделение команды по вагонам.\\n3. **Прорыв:** движение через пассажирские и секционные вагоны, нейтрализация охраны.\\n4. **Взлом:** вскрытие грузового модуля (проверка Хакерство DC 20) и перенос груза.\\n5. **Эвакуация:** синхронный прыжок на ховербайки перед прибытием в Осаку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт:** 3 000 XP.\\n- **Финансы:** 50 000 еддисов, дополнительные бонусы за сохранение целостности груза.\\n- **Добыча:** зашифрованные корпоративные данные, которые можно продать или использовать в сюжетах Relic.\\n- **Ачивка:** «Пуля в Пути» — открывает тематические косметические предметы и трофеи.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые особенности", "body": "- **Движущаяся локация:** поезд движется со скоростью 500 км/ч, создавая ограничения и визуальные эффекты.\\n- **Тайм-менеджмент:** каждая сцена ограничена коротким таймером, заставляя игроков распределять задачи.\\n- **Кооперация:** команды 2-4 игроков должны делить роли — взлом, штурм, удержание, пилот эвакуации.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Синкансен", "definition": "Сеть высокоскоростных поездов в Японии; в мире NECPGAME разгоняется до 500 км/ч."}], "references": [{"title": "JR East Shinkansen Overview", "link": "https://www.jreast.co.jp/e/shinkansen/"}], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "lore_team", "changes": "Перенос описания «Ограбление Синкансена» в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

