INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-rising-sun', 'Токио 2078-2093 — Восходящее Солнце', 'Игрок выбирает наследие предыдущей лиги и встречает новый рассвет на вершине Фудзи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-rising-sun", "title": "Токио 2078-2093 — Восходящее Солнце", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "legacy", "finale"], "topics": ["league-closure", "emotional-endings"], "related_systems": ["narrative-service", "legacy-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2078-2093/quest-050-rising-sun.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный квест лиги не фиксировал выбор наследия и сцену рассвета на Фудзи в структуре.", "goal": "Сформализовать завершение Токио в YAML, обозначить legacy-решения и кат-сцену «горайко».", "essence": "Игрок выбирает наследие предыдущей лиги и встречает новый рассвет на вершине Фудзи.", "key_points": ["Квест-id TOKYO-2093-050, тип main, сложность easy, длительность 30–60 минут.", "Включает legacy-выбор, ночной подъём и финальную кат-сцену рассвета.", "Завершает все 50 квестов Токио и открывает новую лигу."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Финальный этап лиги Токио: герой поднимается на Фудзи, чтобы встретить рассвет и решить, какие достижения взять в следующую эпоху.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Выбрать наследование ключевых бонусов, экипировки или связей для следующей лиги.\\n2. Добраться до подножия Фудзи и начать ночное восхождение.\\n3. Добраться до вершины, активировать кат-сцену «горайко» и зафиксировать финальный титул.\\n", "mechanics_links": [], "assets": []}, {"id": "legacy_outcome", "title": "Наследие и последствия", "body": "Выбор наследия определяет стартовые условия новой лиги и сохраняет титул «Ветеран Токио 2020-2093». Кат-сцена закрепляет эмоциональное закрытие сюжета.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 266, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация финального квеста «Восходящее Солнце» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

