INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-boso-tribe', 'Токио 2040-2060 — Племя Босодзоку', 'Игрок проходит испытания ночных гонок, модифицирует байк и вступает в подпольную мотобанду Токио.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-boso-tribe", "title": "Токио 2040-2060 — Племя Босодзоку", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-29T12:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "processed_by": "idea-writer", "processed_at": "2025-11-29T12:05:00+00:00", "github_issue_number": 211, "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "bosozoku", "motorcycles"], "topics": ["faction-joining", "street-racing"], "related_systems": ["narrative-service", "racing-system", "reputation-system"], "related_documents": [{"id": "github-issue-211", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/211", "relation": "processed_by", "processed_at": "2025-11-29T12:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2040-2060/quest-027-boso-tribe.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не фиксировал процесс вступления в босодзоку и последствия постоянного розыска.", "goal": "Описать в YAML гонки, кастомизацию байка и фракционные награды.", "essence": "Игрок проходит испытания ночных гонок, модифицирует байк и вступает в подпольную мотобанду Токио.", "key_points": ["Квест-id TOKYO-2060-027, тип faction, сложность medium, длительность 2–4 часа.", "Требует группового участия 5–10 игроков, внедряет полицейский розыск и рейды.", "Даёт доступ к кастомным байкам и фракционным контрактам."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Ночная эстакада заливается неоном; босодзоку демонстрируют экстремальные манёвры, а игроку надо доказать смелость и стиль.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Встретиться с лидером банды на шоссе и получить испытание.\\n2. Пройти гонку с высокими ставками по кольцевой дороге Токио, уклоняясь от полиции.\\n3. Настроить байк, добавив характерные элементы bosozoku.\\n4. Участвовать в ночном рейде и закрепиться в племени.\\n", "mechanics_links": [], "assets": []}, {"id": "risk_reward", "title": "Риски и награды", "body": "Успех приносит 2 000 XP, доступ к кастомному байку и фракции; активируется постоянный полицейский розыск и новые групповые события.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 263, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Племя Босодзоку» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

