INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-j-pop-idol', 'Токио 2061-2077 — J-Pop Идол', 'Игрок проходит прослушивание, тренируется, дебютирует и удерживает вершину Oricon, балансируя между славой и стрессом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-j-pop-idol", "title": "Токио 2061-2077 — J-Pop Идол", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "j-pop", "idol-career"], "topics": ["music-industry", "fame-management"], "related_systems": ["narrative-service", "career-system", "reputation-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-035-j-pop-idol.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание карьеры айдола не уточняло этапы продюсирования, баффы славы и риски скандалов.", "goal": "Структурировать путь J-Pop идола в YAML, связав тренировки, дебют и управление репутацией.", "essence": "Игрок проходит прослушивание, тренируется, дебютирует и удерживает вершину Oricon, балансируя между славой и стрессом.", "key_points": ["Квест-id TOKYO-2077-035, тип social, сложность hard, длительность 4+ часов.", "Включает мини-игры по вокалу и танцам, а также этапы продвижения.", "Успех даёт высокий пассивный доход и +50 к славе, но вводит риск скандалов."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Агентства Токио ищут новых айдолов; игрок строит сценический образ, управляет фан-базой и соблюдает жесткие контракты.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Пройти прослушивание и подписать контракт с продюсером.\\n2. Отточить вокал, танцы и актёрство через мини-игры тренировок.\\n3. Провести дебютный концерт и первую медиатуру.\\n4. Продвигать релизы, взаимодействовать с фанатами и удерживать топ-чарт.\\n", "mechanics_links": [], "assets": []}, {"id": "risk_management", "title": "Риски и репутация", "body": "Игрок отслеживает уровень скандалов, нанимает охрану от сталкеров и выдерживает давление контракта, чтобы сохранить имидж.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Вознаграждение — 5 000 XP, пассивный доход до 500 000 едди в неделю, +50 к славе и уникальные сценические костюмы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «J-Pop Идол» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

