INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seattle-2020-2029-grunge-music', 'Сиэтл 2020-2029 — «Гранж музыка»', 'Игрок посещает MoPOP, легендарные клубы и мемориалы, узнавая о Nirvana, Pearl Jam и других иконах гранжа.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_mopop", "text": "Посещение выставки о гранже: костюмы, инструменты и редкие записи Nirvana, Pearl Jam, Soundgarden и Alice in Chains", "type": "interact", "target": "mopop_grunge_exhibition", "count": 1, "optional": false}, {"id": "play_smells_like_teen_spirit", "text": "Интерактивные стенды позволяют сыграть отрывок Smells Like Teen Spirit", "type": "interact", "target": "interactive_grunge_display", "count": 1, "optional": false}, {"id": "visit_crocodile_club", "text": "Посетить The Crocodile и другие культовые площадки, где зарождался гранж", "type": "interact", "target": "legendary_grunge_clubs", "count": 2, "optional": false}, {"id": "interact_musicians", "text": "В диалогах с музыкантами раскрывается дух 1990-х, субкультура фланелевых рубашек и DIY-записей", "type": "interact", "target": "grunge_musicians_dialogue", "count": 2, "optional": false}, {"id": "visit_kurt_memorial", "text": "Завершение маршрута у мемориала Kurt Cobain. Игрок решает, оставить ли послание и купить коллекционный винил", "type": "interact", "target": "kurt_cobain_memorial", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": -40, "items": [{"item_id": "achievement_grunge_soul", "quantity": 1}], "reputation": {}, "unlocks": ["music_quests", "grunge_culture_access"]}'::jsonb, '[{"branch_id": "grunge_enthusiast", "description": "Стать энтузиастом гранжа", "consequences": {"skills": {"culture": 30, "music": 25}, "reputation": {"Music_Community": 25}, "unlocks": ["exclusive_music_access"]}}]'::jsonb, '{"metadata": {"id": "canon-quest-seattle-2020-2029-grunge-music", "title": "Сиэтл 2020-2029 — «Гранж музыка»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seattle", "quest", "music"], "topics": ["cultural-movement", "music-history"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "liveops-service"], "related_documents": [{"id": "github-issue-1061", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1061", "relation": "migrated_to", "migrated_at": "2025-11-23T02:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/seattle/2020-2029/quest-004-grunge-music.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История гранжа в Сиэтле описана кратко и не даёт игроку культурного опыта.", "goal": "Создать квест, погружающий в музыкальную сцену 1990-х через музеи, клубы и встречи с фанатами.", "essence": "Игрок посещает MoPOP, легендарные клубы и мемориалы, узнавая о Nirvana, Pearl Jam и других иконах гранжа.", "key_points": ["MoPOP хранит экспозицию о гранже и Kurt Cobain.", "Легендарные клубы The Crocodile и другие площадки доступны для посещения.", "Квест завершает покупка винила и получение ачивки «Гранжевая душа»."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_mopop", "text": "Посещение выставки о гранже: костюмы, инструменты и редкие записи Nirvana, Pearl Jam, Soundgarden и Alice in Chains", "type": "interact", "target": "mopop_grunge_exhibition", "count": 1, "optional": false}, {"id": "play_smells_like_teen_spirit", "text": "Интерактивные стенды позволяют сыграть отрывок Smells Like Teen Spirit", "type": "interact", "target": "interactive_grunge_display", "count": 1, "optional": false}, {"id": "visit_crocodile_club", "text": "Посетить The Crocodile и другие культовые площадки, где зарождался гранж", "type": "interact", "target": "legendary_grunge_clubs", "count": 2, "optional": false}, {"id": "interact_musicians", "text": "В диалогах с музыкантами раскрывается дух 1990-х, субкультура фланелевых рубашек и DIY-записей", "type": "interact", "target": "grunge_musicians_dialogue", "count": 2, "optional": false}, {"id": "visit_kurt_memorial", "text": "Завершение маршрута у мемориала Kurt Cobain. Игрок решает, оставить ли послание и купить коллекционный винил", "type": "interact", "target": "kurt_cobain_memorial", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": -40, "items": [{"item_id": "achievement_grunge_soul", "quantity": 1}], "reputation": {}, "unlocks": ["music_quests", "grunge_culture_access"]}, "branches": [{"branch_id": "grunge_enthusiast", "description": "Стать энтузиастом гранжа", "consequences": {"skills": {"culture": 30, "music": 25}, "reputation": {"Music_Community": 25}, "unlocks": ["exclusive_music_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока по ключевым точкам гранж-сцены Сиэтла: музей поп-культуры, клубы и мемориалы. Через музыку,\\nинтервью и артефакты игрок осознаёт влияние гранжа на мировую культуру.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "mopop", "title": "Museum of Pop Culture", "body": "Посещение выставки о гранже: костюмы, инструменты и редкие записи Nirvana, Pearl Jam, Soundgarden и Alice in Chains.\\nИнтерактивные стенды позволяют сыграть отрывок Smells Like Teen Spirit.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "clubs", "title": "Клубы и сцены", "body": "Игрок посещает The Crocodile и другие культовые площадки, где зарождался гранж. В диалогах с музыкантами раскрывается\\nдух 1990-х, субкультура фланелевых рубашек и DIY-записей.\\n", "mechanics_links": ["mechanics/social/social-dialogue.yaml"], "assets": []}, {"id": "memorial", "title": "Мемориал Курта Кобейна", "body": "Завершение маршрута у мемориала Kurt Cobain. Игрок решает, оставить ли послание и купить коллекционный винил или\\nограничиться наблюдением.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, культура +25, расходы -40.\\n- Ачивка «Гранжевая душа» и открытие дополнительных музыкальных квестов.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1061, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики музыкального квеста, гранж-культуры и истории музыки."}, {"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Гранж музыка» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objective