INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-love-hotel', 'Токио 2061-2077 — Love Hotel', 'Игрок бронирует тематический номер, проводит романтическое время и укрепляет связь с партнёром.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-love-hotel", "title": "Токио 2061-2077 — Love Hotel", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["tokyo", "romance", "hospitality"], "topics": ["relationships", "slice-of-life"], "related_systems": ["narrative-service", "romance-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-037-love-hotel.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Документ love hotel не содержал структурированных этапов, наград и связи с романтической системой.", "goal": "Описать квест в YAML с акцентом на выбор комнат, взаимодействие с партнёром и баффы отношений.", "essence": "Игрок бронирует тематический номер, проводит романтическое время и укрепляет связь с партнёром.", "key_points": ["Квест-id TOKYO-2077-037, тип social, сложность easy, длительность 30–60 минут.", "Предусмотрены тематические комнаты и совместная сцена с NPC или игроком.", "Награды включают бафф счастья и +10 к романс-статусу."]}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Love hotels Синдзюку предлагают автоматизированный чек-ин, неоновые коридоры и тематические апартаменты от самурайских до космических мотивов.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Найти подходящий отель и выбрать комнату через AR-панель.\\n2. Настроить атмосферу, активировать караоке и сервисы комнаты.\\n3. Провести время с партнёром, запуская сцену романтического взаимодействия.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 500 XP, тратит 3 000 едди, получает +10 к романс-статусу, +20% к счастью на 24 часа и доступ к особым диалогам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертация квеста «Love Hotel» в YAML и завершение цикла Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

