INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-seattle-2029-mount-rainier', 'Сиэтл 2020-2029 — Поход к горе Рейнир', 'Группа игроков проходит маршруты Paradise и Skyline Trail, исследует ледники и анализирует риски.', 'main', 15, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "arrive_paradise_visitor_center", "text": "Добраться до визит-центра Paradise и получить информацию от рейнджеров о состоянии троп", "type": "interact", "target": "paradise_visitor_center", "count": 1, "optional": false}, {"id": "hike_skyline_trail", "text": "Пройти Skyline Trail, собрав данные о флоре, фауне и ледниках", "type": "explore", "target": "skyline_trail", "count": 1, "optional": false}, {"id": "study_ice_fields", "text": "Изучить ледовые поля и опасные участки, оценить экипировку и погодные условия", "type": "investigate", "target": "ice_fields_hazards", "count": 1, "optional": false}, {"id": "analyze_eruption_scenarios", "text": "Провести анализ сценариев извержения и их влияния на Сиэтл", "type": "investigate", "target": "eruption_scenario_analysis", "count": 1, "optional": false}, {"id": "document_panorama", "text": "Зафиксировать панораму города и Олимпийских гор с высоты", "type": "interact", "target": "mountain_panorama", "count": 1, "optional": false}]'::jsonb, '{"experience": 4000, "money": -50, "items": [{"item_id": "achievement_rainier_conqueror", "quantity": 1}], "reputation": {}, "unlocks": ["nature_quests", "volcano_monitoring_access"]}'::jsonb, '[{"branch_id": "mountaineer_expert", "description": "Стать экспертом по альпинизму", "consequences": {"skills": {"nature": 35, "survival": 30}, "reputation": {"National_Parks": 30}, "unlocks": ["exclusive_nature_access"]}}]'::jsonb, '{"metadata": {"id": "quest-seattle-2029-mount-rainier", "title": "Сиэтл 2020-2029 — Поход к горе Рейнир", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seattle", "nature", "volcano"], "topics": ["outdoor_activity", "natural_risk"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "survival-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1063", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1063", "relation": "migrated_to", "migrated_at": "2025-11-23T02:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/seattle/2020-2029/quest-006-mount-rainier.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природная линия Сиэтла не демонстрировала стратегическую угрозу от активного вулкана Mount Rainier.", "goal": "Воссоздать экспедицию к парку, показать красоту ледников и потенциальные последствия извержения.", "essence": "Группа игроков проходит маршруты Paradise и Skyline Trail, исследует ледники и анализирует риски.", "key_points": ["Mount Rainier — действующий стратовулкан высотой 4 392 м, доминирующий над горизонтом Сиэтла.", "Национальный парк сочетает альпийские луга Paradise и снежные поля, доступные круглый год.", "Извержение способно повлиять на мегаполис; квест акцентирует внимание на мониторинге и эвакуации."]}, "quest_definition": {"quest_type": "main", "level_min": 15, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "arrive_paradise_visitor_center", "text": "Добраться до визит-центра Paradise и получить информацию от рейнджеров о состоянии троп", "type": "interact", "target": "paradise_visitor_center", "count": 1, "optional": false}, {"id": "hike_skyline_trail", "text": "Пройти Skyline Trail, собрав данные о флоре, фауне и ледниках", "type": "explore", "target": "skyline_trail", "count": 1, "optional": false}, {"id": "study_ice_fields", "text": "Изучить ледовые поля и опасные участки, оценить экипировку и погодные условия", "type": "investigate", "target": "ice_fields_hazards", "count": 1, "optional": false}, {"id": "analyze_eruption_scenarios", "text": "Провести анализ сценариев извержения и их влияния на Сиэтл", "type": "investigate", "target": "eruption_scenario_analysis", "count": 1, "optional": false}, {"id": "document_panorama", "text": "Зафиксировать панораму города и Олимпийских гор с высоты", "type": "interact", "target": "mountain_panorama", "count": 1, "optional": false}], "rewards": {"experience": 4000, "money": -50, "items": [{"item_id": "achievement_rainier_conqueror", "quantity": 1}], "reputation": {}, "unlocks": ["nature_quests", "volcano_monitoring_access"]}, "branches": [{"branch_id": "mountaineer_expert", "description": "Стать экспертом по альпинизму", "consequences": {"skills": {"nature": 35, "survival": 30}, "reputation": {"National_Parks": 30}, "unlocks": ["exclusive_nature_access"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Экспедиционный квест ведёт игрока из Сиэтла в Mount Rainier National Park, раскрывая геологию вулкана, историю\\nизвержений и туристическую привлекательность маршрутов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до визит-центра Paradise и получить информацию от рейнджеров о состоянии троп.\\n2. Пройти Skyline Trail, собрав данные о флоре, фауне и ледниках.\\n3. Изучить ледовые поля и опасные участки, оценить экипировку и погодные условия.\\n4. Провести анализ сценариев извержения и их влияния на Сиэтл.\\n5. Зафиксировать панораму города и Олимпийских гор с высоты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Mount Rainier — часть Каскадной гряды, его ледники питают реки региона. Локация важна для местных племён и служит\\nориентиром для жителей Сиэтла.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 4 000 XP\\n- Расходы: 50 единиц валюты\\n- Атрибут «Природа»: +30\\n- Ачивка: «Покоритель Рейнира»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест комбинирует треккинговые, выживающие и исследовательские механики, влияя на мировое состояние при активации\\nсценариев природных угроз.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с природной линией Сиэтла, миссиями мониторинга Каскадных вулканов и сюжетами о климатических рисках.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1063, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики природного квеста, альпинизма и вулканической угрозы."}, {"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Поход к горе Рейнир» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requi