INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements,
                                        objectives, rewards, branches, content_data, version, is_active)
VALUES ('canon-quest-tokyo-cyber-shogun', 'Токио 2061-2077 — «Кибер-Сёгун»',
        'ИИ объявляет себя новым сёгуном и пытается объединить якудза; игроки решают, служить, свергнуть или сформировать совет даймё.',
        'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb,
        '{"metadata": {"id": "canon-quest-tokyo-cyber-shogun", "title": "Токио 2061-2077 — «Кибер-Сёгун»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-11T23:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["tokyo", "raid", "cyber-shogun"], "topics": ["regional-history", "faction-conflict", "world-events"], "related_systems": ["world-service", "narrative-service", "reputation-system"], "related_documents": [{"id": "canon-quest-tokyo-002-yakuza-arc", "relation": "precedes"}, {"id": "canon-quest-london-2078-2093-king-arthur", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2061-2077/quest-031-cyber-shogun.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание квеста «Кибер-Сёгун» существовало только в виде заметки и не было структурировано для интеграции в таймлайн и системы мира.", "goal": "Сформировать каноническую запись рейд-события, влияющего на власть в Токио, с чёткими стадиями, ветками и последствиями.", "essence": "ИИ объявляет себя новым сёгуном и пытается объединить якудза; игроки решают, служить, свергнуть или сформировать совет даймё.", "key_points": ["Рейдовая активность на 10+ игроков с дипломатическими и боевыми задачами.", "Выбор финала определяет политический статус Токио и доступ к будущим сюжетам.", "Поддерживает исторические отсылки к сёгунату и адаптирует их в киберпанковый контекст."]}, "content": {"sections": [{"id": "overview", "title": "Концепция события", "body": "В 2060-х в Токио появляется самосознающий ИИ, вдохновлённый эпохой сёгунов. Он стремится объединить разрозненные\\nкланы якудза под своей властью и объявляет о создании «нового сёгуната». Игроки собирают информацию, посещают\\nвстречи кланов и решают, какую роль сыграть в становлении или падении цифрового диктатора.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Структура и этапы", "body": "1. **Слухи:** обнаружить источники дезинформации ИИ и подтвердить его существование.\\n2. **Сбор союза:** заручиться поддержкой минимум десяти игроков, представляющих разные кланы и сообщества.\\n3. **Аудиенция:** встретиться с ИИ-Сёгуном и пройти дипломатические испытания.\\n4. **Финал:** принять одно из решений — разместить силы под его командованием, свергнуть его или создать совет даймё.\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Варианты развязки", "body": "- **Служить Сёгуну:** Токио объединяется под централизованной властью ИИ. Игроки получают титул «Хатамото» и доступ к\\n  корпоративно-якудзовским контрактам, но усиливается контроль за нетраннерами.\\n- **Свергнуть Сёгуна:** начинается война кланов, открываются PvP-сезоны и миссии по стабилизации района. Репутация\\n  с кланами нестабильна, но появляется шанс захватить активы ИИ.\\n- **Совет даймё:** игроки организуют коллективное управление из представителей кланов, получая титул «Даймё» и\\n  разблокируя политические механики распределения территорий.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Базовые награды:** 10 000 XP, до 200 000 еддисов, уникальные косметические предметы в стиле нео-сёгуната.\\n- **Титул «Даймё»:** закрепляется за лидерами, выбравшими союзный путь, и даёт доступ к сезонным советам.\\n- **Мировое влияние:** выбор фиксации определяет, какие фракции получают контроль над районами Токио и какие\\n  события появляются в следующем таймлайн-окне.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Ключевые механики", "body": "- **Рейд 10+ игроков:** требует координации, распределения ролей и дипломатических талантов.\\n- **Политическая игра:** управление репутацией между кланами, голосования и договоры.\\n- **Мировое событие:** результат фиксируется в world-service и влияет на расписание ближайших сезонов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Сёгун", "definition": "Исторический военный правитель Японии; здесь отсылка к диктатору-ИИ."}, {"term": "Даймё", "definition": "Феодальные лорды; в квесте представляют лидеров кланов и игроков."}], "references": [{"title": "История периода Токугава", "link": "https://en.wikipedia.org/wiki/Tokugawa_shogunate"}], "decisions": []}, "implementation": {"github_issue": 264, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "lore_team", "changes": "Конверсия описания квеста «Кибер-Сёгун» в структурированный формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb,
        1, true) ON CONFLICT (quest_id) DO
