-- Quest: canon-quest-toronto-2020-2029-multiculturalism
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-toronto-2020-2029-multiculturalism', 'Торонто 2020-2029 — «Мультикультурализм»', 'Игрок посещает Chinatown, Little Italy, Greektown, Little India и Kensington Market, чтобы увидеть, как разные культуры сосуществуют.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_chinatown", "text": "Посетить Chinatown, изучить уличные рынки и попробовать димсамы", "type": "interact", "target": "toronto_chinatown", "count": 1, "optional": false}, {"id": "explore_little_italy", "text": "Посетить Little Italy, попробовать итальянский кофе и встретиться с музыкантами", "type": "interact", "target": "toronto_little_italy", "count": 1, "optional": false}, {"id": "explore_greektown", "text": "Посетить Greektown, дегустировать сувлаки и принять участие в музыкальном вечере", "type": "interact", "target": "toronto_greektown", "count": 1, "optional": false}, {"id": "explore_little_india", "text": "Посетить Little India, изучить рынок специй и принять участие в сессии хенны", "type": "interact", "target": "toronto_little_india", "count": 1, "optional": false}, {"id": "explore_kensington_market", "text": "Посетить Kensington Market и заполнить дневник наблюдений о городской мозаике", "type": "interact", "target": "toronto_kensington_market", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "items": [{"item_id": "achievement_citizen_of_the_world", "quantity": 1}], "reputation": {"Toronto_Multicultural_Community": 20, "Toronto_Cultural_Community": 15}, "unlocks": ["cultural_activities_toronto", "integration_storylines"]}'::jsonb, '[{"branch_id": "mosaic_path", "description": "Выбрать философию мозаики (сохранение идентичности)", "consequences": {"skills": {"cultural": 15}, "reputation": {"Toronto_Multicultural_Community": 25}}}]'::jsonb, '{"metadata": {"id": "canon-quest-toronto-2020-2029-multiculturalism", "title": "Торонто 2020-2029 — «Мультикультурализм»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["toronto", "quest", "multiculturalism"], "topics": ["cultural-diversity", "urban-life"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "tourism-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/toronto/2020-2029/quest-004-multiculturalism.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Файл квеста о мультикультурном опыте Торонто хранился в Markdown без структуры для нарративной команды.", "goal": "Стандартизировать маршрут по этническим районам Торонто, подчёркивая «мозаичную» идентичность города.", "essence": "Игрок посещает Chinatown, Little Italy, Greektown, Little India и Kensington Market, чтобы увидеть, как разные культуры сосуществуют.", "key_points": ["Этнические районы дают уникальные микро-сюжеты и разные типы социального взаимодействия.", "Через геймплей игрок осмысливает концепцию «мозаики» вместо «плавильного котла».", "Квест усиливает культурную репутацию и открывает доступ к дополнительным социальным линиям Торонто."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_chinatown", "text": "Посетить Chinatown, изучить уличные рынки и попробовать димсамы", "type": "interact", "target": "toronto_chinatown", "count": 1, "optional": false}, {"id": "explore_little_italy", "text": "Посетить Little Italy, попробовать итальянский кофе и встретиться с музыкантами", "type": "interact", "target": "toronto_little_italy", "count": 1, "optional": false}, {"id": "explore_greektown", "text": "Посетить Greektown, дегустировать сувлаки и принять участие в музыкальном вечере", "type": "interact", "target": "toronto_greektown", "count": 1, "optional": false}, {"id": "explore_little_india", "text": "Посетить Little India, изучить рынок специй и принять участие в сессии хенны", "type": "interact", "target": "toronto_little_india", "count": 1, "optional": false}, {"id": "explore_kensington_market", "text": "Посетить Kensington Market и заполнить дневник наблюдений о городской мозаике", "type": "interact", "target": "toronto_kensington_market", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -40, "items": [{"item_id": "achievement_citizen_of_the_world", "quantity": 1}], "reputation": {"Toronto_Multicultural_Community": 20, "Toronto_Cultural_Community": 15}, "unlocks": ["cultural_activities_toronto", "integration_storylines"]}, "branches": [{"branch_id": "mosaic_path", "description": "Выбрать философию мозаики (сохранение идентичности)", "consequences": {"skills": {"cultural": 15}, "reputation": {"Toronto_Multicultural_Community": 25}}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с мультикультурным ядром Торонто. Игрок двигается по ключевым этническим районам,\\nсобирает впечатления и отмечает различия между ними, формируя дневник наблюдений о городской «мозаике».\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Chinatown — знакомство с китайской культурой\\nЛокация: Торонто, Chinatown, уличные рынки и рестораны\\nОписание: Игрок знакомится с уличными рынками, димсамами и визуальной доминантой китайских вывесок.\\nЦели:\\n  - Прибыть в Chinatown\\n  - Изучить уличные рынки (специи, овощи, экзотические продукты)\\n  - Попробовать димсамы в традиционном ресторане\\n  - Наблюдать визуальную доминанту китайских вывесок\\n  - Взаимодействовать с местными жителями и торговцами\\n  - Изучить китайские традиции и культуру\\n  - Записать впечатления в дневник наблюдений\\n  - Проверка навыков: Social 0.3+ для взаимодействия, Observation 0.3+ для наблюдений, Awareness 0.3+ для изучения культуры\\n\\nЭтап 2: Little Italy — итальянская культура\\nЛокация: Торонто, Little Italy, кофейни и рестораны\\nОписание: Игрок посещает кофейни с эспрессо и вечерние встречи с локальными музыкантами.\\nЦели:\\n  - Прибыть в Little Italy\\n  - Посетить традиционную кофейню с эспрессо\\n  - Попробовать итальянский кофе\\n  - Встретиться с локальными музыкантами\\n  - Изучить итальянские традиции и культуру\\n  - Взаимодействовать с местными жителями\\n  - Записать впечатления в дневник наблюдений\\n  - Проверка навыков: Social 0.3+ для взаимодействия, Appreciation 0.3+ для наслаждения музыкой, Cultural 0.3+ для изучения культуры\\n\\nЭтап 3: Greektown (Danforth) — греческая культура\\nЛокация: Торонто, Greektown (Danforth), рестораны и таверны\\nОписание: Игрок дегустирует сувлаки, знакомится с традиционными напитками и музыкальными вечерами.\\nЦели:\\n  - Прибыть в Greektown (Danforth)\\n  - Дегустировать сувлаки (греческий шашлык)\\n  - Попробовать традиционные греческие напитки\\n  - Принять участие в музыкальном вечере\\n  - Изучить греческие традиции и культуру\\n  - Взаимодействовать с местными жителями\\n  - Записать впечатления в дневник наблюдений\\n  - Проверка навыков: Culinary 0.3+ для дегустации, Social 0.3+ для взаимодействия, Cultural 0.3+ для изучения культуры\\n\\nЭтап 4: Little India — индийская культура\\nЛокация: Торонто, Little India, рынки специй и рестораны\\nОписание: Игрок знакомится со специями, Болливудскими показами и сессиями хенны.\\nЦели:\\n  - Прибыть в Little India\\n  - Изучить рынок специй\\n  - Попробовать индийскую еду\\n  - Посмотреть Болливудский показ (или фрагмент)\\n  - Принять участие в сессии хенны (роспись хной)\\n  - Изучить индийские традиции и культуру\\n  - Взаимодействовать с местными жителями\\n  - Записать впечатления в дневник наблюдений\\n  - Проверка навыков: Observation 0.3+ для изучения специй, Social 0.3+ для взаимодействия, Cultural 0.3+ для изучения культуры\\n\\nЭтап 5: Kensington Market — многообразие культур\\nЛокация: Торонто, Kensington Market, мультикультурный район\\nОписание: Игрок завершает маршрут в Kensington Market, где разные культуры сосуществуют, формируя городскую \\"мозаику\\".\\nЦели:\\n  - Прибыть в Kensington Market\\n  - Наблюдать многообразие культур в одном месте\\n  - Изучить, как разные культуры сосуществуют\\n  - Взаимодействовать с представителями разных культур\\n  - Заполнить дневник наблюдений о городской \\"мозаике\\"\\n  - Завершить квест\\n  - Проверка навыков: Observation 0.4+ для наблюдений, Social 0.4+ для взаимодействия, Cultural 0.4+ для понимания многообразия\\n5. Kensington Market — смешение традиций и культурных артефактов.\\n6. Финальное осмысление «мозаики» в интерактивном блокноте игрока.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Более 50% жителей Торонто родились за пределами Канады.\\n- В городе звучит свыше 180 языков, что отражается в уличном шуме и вывесках.\\n- Муниципальная политика поддерживает мультикультурность как ключевую ценность.\\n- Лозунг «мозаика» подчёркивает сохранение культурных идентичностей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Базовые награды:\\n  - Опыт: 1 500 XP\\n  - Репутация Toronto Multicultural Community: +20\\n  - Репутация Toronto Cultural Community: +15\\n  - Ресурсы культуры: +25\\n  - Валюта: -40 еддис (расходы на еду, напитки, сувениры)\\n  - Ачивка \\"Гражданин мира\\" (базовая)\\n\\nНаграды в зависимости от философии многообразия:\\n  - Мозаика (mosaic): достижение \\"Гражданин мозаики\\", +15 культура, +10 репутация Multicultural Community, доступ к культурным активностям с сохранением идентичности\\n  - Плавильный котёл (melting pot): достижение \\"Гражданин плавильного котла\\", +20 культура, +15 репутация Fusion Community, доступ к фьюжн культурным активностям\\n  - Сбалансированный подход: достижение \\"Гражданин мира\\", +25 культура, баланс репутации, доступ ко всем культурным активностям\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - Достижение \\"Общительный\\" (при успешной проверке Social на этапе 1)\\n  - Достижение \\"Наблюдатель\\" (при успешной проверке Observation на этапе 1)\\n  - Достижение \\"Итальянофил\\" (при успешной проверке Social на этапе 2)\\n  - Достижение \\"Ценитель музыки\\" (при успешной проверке Appreciation)\\n  - Достижение \\"Знаток греческой кухни\\" (при успешной проверке Culinary)\\n  - Достижение \\"Знаток специй\\" (при успешной проверке Observation на этапе 4)\\n  - Достижение \\"Индофил\\" (при успешной проверке Social на этапе 4)\\n  - Достижение \\"Знаток многообразия\\" (при успешной проверке Observation на этапе 5) и +5 XP\\n  - Достижение \\"Межкультурный общительный\\" (при успешной проверке Social на этапе 5) и +5 XP\\n  - Достижение \\"Гражданин мира\\" (при успешной проверке Cultural на этапе 5) и +5 XP\\n\\nУникальные награды:\\n  - Дневник наблюдений о городской \\"мозаике\\" (при заполнении дневника)\\n  - Коллекция \\"Городская мозаика\\" (при успешном завершении всех этапов)\\n  - Доступ к дальнейшим культурным активностям районов (при завершении квеста)\\n  - Доступ к сюжетам о политике интеграции и городском планировании (при успешном завершении)\\n  - Влияние на все последующие культурные квесты в Торонто\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Культурные проверки, фиксирующие знание ритуалов и этикета каждого района.\\n- Социальные взаимодействия с NPC, представляющими разные диаспоры.\\n- Туристические заметки формируют коллекцию «Городская мозаика».\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест развивает культурную линию Торонто и подготавливает игрока к сюжетам о политике интеграции\\nи городском планировании.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1051, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Мультикультурализм» в формат знаний."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлена версия до 2.0.0."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями и целями (5 этапов: Chinatown, Little Italy, Greektown, Little India, Kensington Market), диалоги с NPC (китайский торговец, итальянский бариста, греческий ресторатор, индийский торговец специями, местный житель), проверки навыков (Social, Observation, Awareness, Appreciation, Cultural, Culinary), ветвления и выборы, детальные награды с ачивкой Гражданин мира и ресурсами культуры +25."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

