-- Quest: canon-quest-toronto-2020-2029-niagara-falls
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-toronto-2020-2029-niagara-falls', 'Торонто 2020-2029 — «Поездка к Ниагарскому водопаду»', 'Игрок организует экскурсию на Ниагарский водопад, проходит ключевые смотровые точки и переживает ночную подсветку Horseshoe Falls.', 'side', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_to_niagara_falls", "text": "Организовать поездку из Торонто к Ниагарскому водопаду (около 1.5 часов пути)", "type": "interact", "target": "niagara_falls_trip_organization", "count": 1, "optional": false}, {"id": "explore_canadian_side", "text": "Изучить канадскую сторону водопада и обзорные площадки", "type": "interact", "target": "niagara_falls_canadian_side", "count": 1, "optional": false}, {"id": "observe_horseshoe_falls", "text": "Наблюдать Horseshoe Falls и анализировать геометрию подковы", "type": "interact", "target": "horseshoe_falls_observation", "count": 1, "optional": false}, {"id": "ride_maid_of_the_mist", "text": "Принять участие в лодочной экскурсии Maid of the Mist (опционально)", "type": "interact", "target": "maid_of_the_mist_boat", "count": 1, "optional": true}, {"id": "photograph_rainbow", "text": "Сделать фотографии с радугой у водопада", "type": "interact", "target": "niagara_falls_rainbow_photo", "count": 1, "optional": false}, {"id": "watch_night_lighting", "text": "Наблюдать ночную подсветку водопада и зафиксировать впечатления", "type": "interact", "target": "niagara_falls_night_lighting", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": -40, "items": [{"item_id": "achievement_waterfall_master", "quantity": 1}], "reputation": {"Toronto_Tourism_Community": 20, "Toronto_Nature_Community": 15}, "unlocks": ["natural_tours_access", "canada_nature_line"]}'::jsonb, '[{"branch_id": "extreme_adventurer_path", "description": "Стать экстремальным путешественником через Maid of the Mist", "consequences": {"skills": {"courage": 30, "nature": 30}, "reputation": {"Toronto_Adventure_Community": 25}}}]'::jsonb, '{"metadata": {"id": "canon-quest-toronto-2020-2029-niagara-falls", "title": "Торонто 2020-2029 — «Поездка к Ниагарскому водопаду»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["toronto", "quest", "niagara-falls"], "topics": ["natural-wonders", "tourism"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "exploration-service", "tourism-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/toronto/2020-2029/quest-005-niagara-falls-trip.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут к Ниагарскому водопаду был описан в Markdown и не учитывал геймплейные последствия поездки.", "goal": "Описать последовательность поездки из Торонто к водопаду с упором на атмосферу, природную мощь и систему наград.", "essence": "Игрок организует экскурсию на Ниагарский водопад, проходит ключевые смотровые точки и переживает ночную подсветку Horseshoe Falls.", "key_points": ["Подчёркнут путь из Торонто и важность канадской стороны для визуального эффекта.", "Лодочная экскурсия «Maid of the Mist» и ночная подсветка усиливают впечатления.", "Квест усиливает природную репутацию и связывает Торонто с природной линией Канады."]}, "quest_definition": {"quest_type": "side", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_to_niagara_falls", "text": "Организовать поездку из Торонто к Ниагарскому водопаду (около 1.5 часов пути)", "type": "interact", "target": "niagara_falls_trip_organization", "count": 1, "optional": false}, {"id": "explore_canadian_side", "text": "Изучить канадскую сторону водопада и обзорные площадки", "type": "interact", "target": "niagara_falls_canadian_side", "count": 1, "optional": false}, {"id": "observe_horseshoe_falls", "text": "Наблюдать Horseshoe Falls и анализировать геометрию подковы", "type": "interact", "target": "horseshoe_falls_observation", "count": 1, "optional": false}, {"id": "ride_maid_of_the_mist", "text": "Принять участие в лодочной экскурсии Maid of the Mist (опционально)", "type": "interact", "target": "maid_of_the_mist_boat", "count": 1, "optional": true}, {"id": "photograph_rainbow", "text": "Сделать фотографии с радугой у водопада", "type": "interact", "target": "niagara_falls_rainbow_photo", "count": 1, "optional": false}, {"id": "watch_night_lighting", "text": "Наблюдать ночную подсветку водопада и зафиксировать впечатления", "type": "interact", "target": "niagara_falls_night_lighting", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": -40, "items": [{"item_id": "achievement_waterfall_master", "quantity": 1}], "reputation": {"Toronto_Tourism_Community": 20, "Toronto_Nature_Community": 15}, "unlocks": ["natural_tours_access", "canada_nature_line"]}, "branches": [{"branch_id": "extreme_adventurer_path", "description": "Стать экстремальным путешественником через Maid of the Mist", "consequences": {"skills": {"courage": 30, "nature": 30}, "reputation": {"Toronto_Adventure_Community": 25}}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок выезжает из Торонто на экскурсию к Ниагарскому водопаду, чтобы увидеть одно из главных природных чудес Северной Америки.\\nКвест строится на смене локаций, чувственном восприятии энергии водопада и вечерней кульминации с подсветкой.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Подготовка поездки и выезд из Торонто\\nЛокация: Торонто, центр города, экскурсионный автобус или автомобиль\\nОписание: Игрок организует экскурсию на Ниагарский водопад, выезжая из Торонто. Около 1,5 часов пути.\\nЦели:\\n  - Организовать поездку к Ниагарскому водопаду\\n  - Выбрать способ поездки (экскурсионный автобус или автомобиль)\\n  - Выехать из Торонто\\n  - Проехать около 1,5 часов до Ниагарского водопада\\n  - Изучить историю Ниагарского водопада\\n  - Подготовиться к визиту\\n  - Проверка навыков: Planning 0.3+ для планирования, Awareness 0.3+ для изучения истории\\n\\nЭтап 2: Знакомство с канадской стороной водопада\\nЛокация: Ниагарский водопад, канадская сторона, обзорные площадки\\nОписание: Игрок знакомится с канадской стороной водопада и обзорными площадками. Лучшие панорамы открываются с канадской стороны.\\nЦели:\\n  - Прибыть к Ниагарскому водопаду (канадская сторона)\\n  - Изучить обзорные площадки\\n  - Полюбоваться панорамой водопада с канадской стороны\\n  - Взаимодействовать с гидом или другими туристами\\n  - Изучить инфраструктуру (настроенную на туристов)\\n  - Проверка навыков: Observation 0.3+ для наблюдений, Social 0.3+ для взаимодействия\\n\\nЭтап 3: Наблюдение Horseshoe Falls\\nЛокация: Ниагарский водопад, обзорные площадки Horseshoe Falls\\nОписание: Игрок наблюдает Horseshoe Falls и анализирует геометрию «подковы». Horseshoe Falls обеспечивает основной поток воды и акустический эффект.\\nЦели:\\n  - Прибыть к Horseshoe Falls\\n  - Наблюдать геометрию «подковы» (канадская сторона водопада)\\n  - Наслаждаться акустическим эффектом водопада\\n  - Наблюдать мощь и красоту водного потока\\n  - Изучить природную геологию\\n  - Записать впечатления\\n  - Проверка навыков: Observation 0.4+ для анализа геометрии, Awareness 0.3+ для изучения геологии, Appreciation 0.3+ для наслаждения природой\\n\\nЭтап 4: Лодочная экскурсия «Maid of the Mist»\\nЛокация: Ниагарский водопад, лодка «Maid of the Mist»\\nОписание: Игрок садится на «Maid of the Mist» и проходит через облака брызг. Интенсивность брызг и шум создают условия для сенсорных испытаний.\\nЦели:\\n  - Забронировать билет на «Maid of the Mist»\\n  - Надеть защитные плащи\\n  - Сесть на лодку «Maid of the Mist»\\n  - Подплыть к водопаду\\n  - Пройти через облака брызг\\n  - Преодолеть сенсорные испытания (шум, брызги, интенсивность)\\n  - Испытать мощь водопада вблизи\\n  - Проверка навыков: Courage 0.3+ для преодоления страха, Perception 0.3+ для сенсорных ощущений, Survival 0.3+ для преодоления интенсивности\\n\\nЭтап 5: Фотосессия с радугой\\nЛокация: Ниагарский водопад, обзорные площадки с радугой\\nОписание: Игрок делает фотосессию с радугой и собирает визуальные артефакты. Радуга образуется из-за брызг водопада и солнечного света.\\nЦели:\\n  - Найти место с радугой (образуется от брызг водопада)\\n  - Сделать фотографии с радугой\\n  - Собрать визуальные артефакты (фотографии, заметки)\\n  - Насладиться природной красотой\\n  - Записать впечатления\\n  - Проверка навыков: Photography 0.4+ для качественных фотографий, Observation 0.3+ для наблюдений, Appreciation 0.3+ для наслаждения\\n\\nЭтап 6: Ночная подсветка и фиксация впечатлений\\nЛокация: Ниагарский водопад, обзорные площадки, ночная подсветка\\nОписание: Игрок возвращается на ночную подсветку водопада и фиксирует впечатления в дневнике. Ночная подсветка создаёт атмосферное зрелище.\\nЦели:\\n  - Дождаться вечера и ночи\\n  - Вернуться к обзорным площадкам\\n  - Наблюдать ночную подсветку водопада\\n  - Насладиться атмосферным зрелищем\\n  - Зафиксировать впечатления в дневнике\\n  - Завершить квест\\n  - Проверка навыков: Observation 0.3+ для наблюдений, Writing 0.3+ для записи впечатлений, Appreciation 0.3+ для наслаждения\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml", "mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Лучшие панорамы открываются с канадской стороны, где инфраструктура настроена на туристов.\\n- Horseshoe Falls обеспечивает основной поток воды и акустический эффект.\\n- Пограничный статус локации добавляет контекст взаимоотношений Канады и США.\\n- Интенсивность брызг и шум создают условия для сенсорных испытаний.\\n", "mechanics_links": [], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Экскурсовод (Майк)\\nДиалог 1 (начальный):\\n  - Майк: \\"Welcome to Niagara Falls! You''re about to see one of the world''s greatest natural wonders!\\"\\n  - Игрок: \\"Tell me about the falls.\\"\\n  - Майк: \\"Niagara Falls is actually three waterfalls: Horseshoe Falls, American Falls, and Bridal Veil Falls. Horseshoe Falls is on the Canadian side — that''s where we are. It''s the most powerful and impressive!\\"\\n  - Игрок: \\"Why is the Canadian side better?\\"\\n  - Майк: \\"The Canadian side offers the best views! You can see all three waterfalls from here. The infrastructure is set up for tourists, and the views are spectacular.\\"\\n\\nГид на обзорной площадке (Сара)\\nДиалог 2 (на обзорной площадке):\\n  - Сара: \\"You''re looking at Horseshoe Falls — it''s 57 meters high and 670 meters wide! It''s called Horseshoe because of its curved shape.\\"\\n  - Игрок: \\"How powerful is it?\\"\\n  - Сара: \\"Horseshoe Falls carries about 90% of the Niagara River''s water. That''s 2,400 cubic meters per second! The sound, the mist, the power — it''s overwhelming.\\"\\n  - Игрок: \\"Tell me about the geography.\\"\\n  - Сара: \\"The falls formed about 12,000 years ago after the last Ice Age. The water carved its way through the rock, creating this incredible natural wonder. It''s still eroding, moving upstream!\\"\\n\\nКапитан «Maid of the Mist» (Джон)\\nДиалог 3 (перед посадкой):\\n  - Джон: \\"Welcome aboard Maid of the Mist! This is going to be an incredible experience!\\"\\n  - Игрок: \\"What should I expect?\\"\\n  - Джон: \\"We''ll take you right up to the base of Horseshoe Falls. You''ll be in the mist, you''ll feel the power, you''ll hear the thunder. It''s intense, but safe. Don''t worry, we''ve been doing this for over 170 years!\\"\\n  - Игрок: \\"Is it dangerous?\\"\\n  - Джон: \\"No, it''s completely safe! We have safety protocols, life jackets, experienced crew. You''ll get wet from the mist, but that''s part of the experience!\\"\\n\\nФотограф (Эмили)\\nДиалог 4 (фотосессия):\\n  - Эмили: \\"Perfect timing! The rainbow is visible now. The mist from the falls creates beautiful rainbows when the sun hits it!\\"\\n  - Игрок: \\"How often do you see rainbows?\\"\\n  - Эмили: \\"On sunny days, rainbows are common here. The mist from the falls refracts the sunlight, creating these beautiful arcs. It''s nature''s art!\\"\\n  - Игрок: \\"What''s the best time for photos?\\"\\n  - Эмили: \\"Morning and afternoon are great for rainbows. Evening is perfect for the lighting. And night... wait until you see the night lighting! It''s magical!\\"\\n\\nМестный житель (Анри)\\nДиалог 5 (ночная подсветка):\\n  - Анри: \\"Welcome back for the night lighting! The falls are illuminated every night. It''s a tradition that started in 1860!\\"\\n  - Игрок: \\"Tell me about the lighting.\\"\\n  - Анри: \\"The falls are lit with colored lights — red, blue, green, white. It creates this incredible spectacle. Each color has meaning: red for Canada, blue for peace, green for nature...\\"\\n  - Игрок: \\"It''s beautiful!\\"\\n  - Анри: \\"Yes, but it''s more than beauty. It''s about respecting nature, honoring this wonder. The falls are powerful, and the lighting reminds us of that power.\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Способ поездки к водопаду\\nУсловие: При организации поездки\\nВарианты:\\n  - Экскурсионный автобус: 80 еддис, включает гида, безопасно и комфортно, разблокируется дополнительная информация\\n  - Аренда автомобиля: 60 еддис (топливо и аренда), свобода, но без гида\\n  - С попутчиками: 40 еддис, дешевле, но меньше контроля\\n\\nВыбор 2: Подход к обзорным площадкам\\nУсловие: На канадской стороне\\nВарианты:\\n  - Интенсивное изучение: посетить все обзорные площадки, больше времени, но больше информации, разблокируется дополнительная информация о геологии\\n  - Быстрое изучение: посетить основные площадки, меньше времени, но стандартная информация\\n  - Сбалансированный подход: оптимальный баланс\\n\\nВыбор 3: Участие в «Maid of the Mist» (критический выбор)\\nУсловие: Перед лодочной экскурсией\\nВарианты:\\n  - Принять участие в «Maid of the Mist»: экстремальный опыт, разблокируется достижение \\"Повелитель водопадов\\", максимальный опыт, +10 XP, дополнительные визуальные артефакты\\n  - Наблюдать с берега: безопасный выбор, стандартный опыт, базовая ачивка\\n\\nВыбор 4: Подход к фотосессии\\nУсловие: При фотосессии с радугой\\nВарианты:\\n  - Интенсивная фотосессия: много фотографий, разблокируется достижение \\"Фотограф водопада\\", коллекция фотографий\\n  - Стандартная фотосессия: несколько фотографий, стандартный опыт\\n  - Минимальная фотосессия: одна-две фотографии, базовый опыт\\n", "mechanics_links": [], "assets": []}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Planning (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок эффективно планирует поездку, экономит время и деньги, разблокируется достижение \\"Планировщик\\"\\nПровал: Игрок планирует стандартно\\n\\nПроверка 1b: Awareness (Этап 1)\\nТребование: 0.3+\\nУспех: Игрок глубоко изучает историю Ниагарского водопада, разблокируется дополнительная информация о водопаде\\nПровал: Игрок получает только базовую информацию\\n\\nПроверка 2: Observation (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за панорамой, разблокируется дополнительная информация о водопаде и разблокируется достижение \\"Наблюдатель\\"\\nПровал: Игрок получает только общее впечатление\\n\\nПроверка 2b: Social (Этап 2)\\nТребование: 0.3+\\nУспех: Игрок успешно взаимодействует с гидом или туристами, получает дополнительную информацию и разблокируется достижение \\"Общительный\\"\\nПровал: Игрок получает стандартное взаимодействие\\n\\nПроверка 3: Observation (Этап 3)\\nТребование: 0.4+\\nУспех: Игрок успешно анализирует геометрию «подковы», разблокируется дополнительная информация о геологии и разблокируется достижение \\"Знаток геологии\\" и +5 XP\\nПровал: Игрок получает только базовое понимание\\n\\nПроверка 3b: Awareness (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок глубоко изучает природную геологию, разблокируется дополнительная информация о формировании водопада\\nПровал: Игрок получает только базовую информацию\\n\\nПроверка 3c: Appreciation (Этап 3)\\nТребование: 0.3+\\nУспех: Игрок глубоко наслаждается природой, получает дополнительный бафф и разблокируется достижение \\"Ценитель природы\\"\\nПровал: Игрок получает стандартное впечатление\\n\\nПроверка 4: Courage (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно преодолевает страх на «Maid of the Mist», разблокируется достижение \\"Храбрый\\" и +5 XP\\nПровал: Игрок испытывает страх, получает дебафф \\"Боязнь воды\\" на 1 час\\n\\nПроверка 4b: Perception (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно переживает сенсорные ощущения, разблокируется дополнительная информация о мощности водопада и разблокируется достижение \\"Сенсорный мастер\\"\\nПровал: Игрок получает стандартные ощущения\\n\\nПроверка 4c: Survival (Этап 4)\\nТребование: 0.3+\\nУспех: Игрок успешно преодолевает интенсивность брызг и шума, разблокируется достижение \\"Выживший в брызгах\\"\\nПровал: Игрок испытывает трудности, получает дебафф \\"Головокружение\\" на 30 минут\\n\\nПроверка 5: Photography (Этап 5)\\nТребование: 0.4+\\nУспех: Игрок делает качественные фотографии с радугой, разблокируется достижение \\"Фотограф водопада\\" и коллекция фотографий и +5 XP\\nПровал: Игрок делает стандартные фотографии\\n\\nПроверка 5b: Observation (Этап 5)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за радугой, разблокируется дополнительная информация о природных явлениях\\nПровал: Игрок получает только базовое впечатление\\n\\nПроверка 5c: Appreciation (Этап 5)\\nТребование: 0.3+\\nУспех: Игрок глубоко наслаждается природной красотой, получает дополнительный бафф\\nПровал: Игрок получает стандартное впечатление\\n\\nПроверка 6: Observation (Этап 6)\\nТребование: 0.3+\\nУспех: Игрок внимательно наблюдает за ночной подсветкой, разблокируется дополнительная информация и разблокируется достижение \\"Знаток подсветки\\"\\nПровал: Игрок получает только базовое впечатление\\n\\nПроверка 6b: Writing (Этап 6)\\nТребование: 0.3+\\nУспех: Игрок создаёт качественный дневник впечатлений, разблокируется дополнительная информация и разблокируется достижение \\"Писатель впечатлений\\" и +5 XP\\nПровал: Игрок создаёт стандартный дневник\\n\\nПроверка 6c: Appreciation (Этап 6)\\nТребование: 0.3+\\nУспех: Игрок глубоко наслаждается атмосферным зрелищем, получает дополнительный бафф и разблокируется достижение \\"Ценитель атмосферы\\"\\nПровал: Игрок получает стандартное впечатление\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Базовые награды:\\n  - Опыт: 3 000 XP\\n  - Репутация Toronto Tourism Community: +20\\n  - Репутация Toronto Nature Community: +15\\n  - Природа: +25\\n  - Валюта: -40 до -80 еддис (в зависимости от способа поездки и участия в «Maid of the Mist»)\\n  - Ачивка \\"Повелитель водопадов\\" (базовая)\\n\\nНаграды в зависимости от участия в «Maid of the Mist»:\\n  - Участие в «Maid of the Mist»: достижение \\"Повелитель водопадов\\", максимальный опыт, +10 XP, дополнительные визуальные артефакты, максимальная природа (+30)\\n  - Наблюдение с берега: стандартная природа (+25), базовая ачивка\\n\\nНаграды за подход к фотосессии:\\n  - Интенсивная фотосессия: достижение \\"Фотограф водопада\\", коллекция фотографий, дополнительные визуальные артефакты\\n  - Стандартная фотосессия: несколько фотографий, стандартные артефакты\\n  - Минимальная фотосессия: одна-две фотографии, базовые артефакты\\n\\nДополнительные награды (при успешных проверках навыков):\\n  - Достижение \\"Планировщик\\" (при успешной проверке Planning)\\n  - Экономия времени и денег (при успешной проверке Planning)\\n  - Дополнительная информация о водопаде (при успешной проверке Awareness)\\n  - Достижение \\"Наблюдатель\\" (при успешной проверке Observation на этапе 2)\\n  - Дополнительная информация о водопаде (при успешной проверке Observation на этапе 2)\\n  - Достижение \\"Общительный\\" (при успешной проверке Social)\\n  - Дополнительная информация (при успешной проверке Social)\\n  - Достижение \\"Знаток геологии\\" (при успешной проверке Observation на этапе 3)\\n  - Дополнительная информация о геологии (при успешной проверке Observation на этапе 3)\\n  - +5 XP (при успешной проверке Observation на этапе 3)\\n  - Дополнительная информация о формировании водопада (при успешной проверке Awareness на этапе 3)\\n  - Достижение \\"Ценитель природы\\" (при успешной проверке Appreciation на этапе 3)\\n  - Дополнительный бафф (при успешной проверке Appreciation на этапе 3)\\n  - Достижение \\"Храбрый\\" (при успешной проверке Courage)\\n  - +5 XP (при успешной проверке Courage)\\n  - Достижение \\"Сенсорный мастер\\" (при успешной проверке Perception)\\n  - Дополнительная информация о мощности водопада (при успешной проверке Perception)\\n  - Достижение \\"Выживший в брызгах\\" (при успешной проверке Survival)\\n  - Достижение \\"Фотограф водопада\\" (при успешной проверке Photography)\\n  - Коллекция фотографий (при успешной проверке Photography)\\n  - +5 XP (при успешной проверке Photography)\\n  - Дополнительная информация о природных явлениях (при успешной проверке Observation на этапе 5)\\n  - Дополнительный бафф (при успешной проверке Appreciation на этапе 5)\\n  - Достижение \\"Знаток подсветки\\" (при успешной проверке Observation на этапе 6)\\n  - Дополнительная информация (при успешной проверке Observation на этапе 6)\\n  - Достижение \\"Писатель впечатлений\\" (при успешной проверке Writing)\\n  - +5 XP (при успешной проверке Writing)\\n  - Достижение \\"Ценитель атмосферы\\" (при успешной проверке Appreciation на этапе 6)\\n  - Дополнительный бафф (при успешной проверке Appreciation на этапе 6)\\n\\nНаграды за способ поездки:\\n  - Экскурсионный автобус: дополнительная информация, достижение \\"Турист с гидом\\"\\n  - Аренда автомобиля: свобода, экономия (если путешествует с друзьями)\\n  - С попутчиками: экономия денег, достижение \\"Экономный путешественник\\"\\n\\nНаграды за подход к обзорным площадкам:\\n  - Интенсивное изучение: дополнительная информация о геологии, достижение \\"Исследователь геологии\\"\\n  - Быстрое изучение: экономия времени\\n  - Сбалансированный подход: оптимальный баланс\\n\\nУникальные награды:\\n  - Визуальные артефакты (фотографии с радугой, фотографии ночной подсветки, фотографии «Maid of the Mist») — собираются при успешных проверках\\n  - Дневник впечатлений о Ниагарском водопаде (при заполнении дневника)\\n  - Разблокировка дополнительных природных туров (при успешном завершении)\\n  - Доступ к природной линии Канады (при завершении квеста)\\n  - Влияние на все последующие природные и туристические квесты в Торонто и Канаде\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Сенсорные проверки, фиксирующие реакцию на шум и брызги.\\n- Туристические мета-задачи: фото-коллекции, отметки смотровых площадок.\\n- Управление временем суток для выбора оптимальных впечатлений.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест продолжает природную линию Канады и помогает связать урбанистическую кампанию Торонто с природными чудесами региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1052, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Поездка к Ниагарскому водопаду» в формат знаний."}, {"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлена версия до 2.0.0."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями и целями (6 этапов: подготовка поездки, канадская сторона, Horseshoe Falls, Maid of the Mist, фотосессия с радугой, ночная подсветка), диалоги с NPC (экскурсовод, гид, капитан, фотограф, местный житель), проверки навыков (Planning, Awareness, Observation, Social, Appreciation, Courage, Perception, Survival, Photography, Writing), ветвления и выборы, детальные награды с ачивкой Повелитель водопадов и природа +25-30."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

