INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-toronto-2020-2029-sorry-culture', 'Торонто 2020-2029 — «Культура «Sorry»»', 'Игрок исследует повседневные взаимодействия, фиксирует паттерны вежливости и учится интегрироваться в культурный код Канады.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "observe_polite_interactions", "text": "Наблюдать повседневные взаимодействия в общественных пространствах и фиксировать паттерны вежливости", "type": "interact", "target": "public_spaces_toronto", "count": 1, "optional": false}, {"id": "understand_sorry_culture", "text": "Изучить канадскую культуру извинений и понять её культурный код", "type": "interact", "target": "cultural_observation", "count": 1, "optional": false}, {"id": "experiment_with_norms", "text": "Экспериментировать с нарушением норм и наблюдать реакцию NPC", "type": "choice", "target": "norm_experimentation", "count": 1, "optional": true}]'::jsonb, '{"experience": 800, "money": 0, "items": [{"item_id": "achievement_polite_citizen", "quantity": 1}], "reputation": {"Toronto_Social_Community": 15}, "unlocks": ["canadian_npc_dialogues", "social_quests_toronto"]}'::jsonb, '[{"branch_id": "cultural_integration_path", "description": "Успешно интегрироваться в канадский культурный код", "consequences": {"skills": {"social": 20, "diplomacy": 15}, "reputation": {"Toronto_Social_Community": 25}}}]'::jsonb, '{"metadata": {"id": "canon-quest-toronto-2020-2029-sorry-culture", "title": "Торонто 2020-2029 — «Культура «Sorry»»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["toronto", "quest", "social-culture"], "topics": ["etiquette", "slice-of-life"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "github-issue-1053", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1053", "relation": "migrated_to", "migrated_at": "2025-11-23T02:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/toronto/2020-2029/quest-006-sorry-culture.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Оригинальный текст про культуру канадского «sorry» не содержал геймплейной структуры и не объяснял ценность для социальной линии.", "goal": "Сформировать квест наблюдений за канадской вежливостью и показать влияние поведения игрока на репутацию.", "essence": "Игрок исследует повседневные взаимодействия, фиксирует паттерны вежливости и учится интегрироваться в культурный код Канады.", "key_points": ["Наблюдения в общественных пространствах помогают игроку понять, как работает канадская культура извинений.", "Эксперименты с нарушением норм демонстрируют реакцию NPC и влияют на социальные рейтинги.", "Квест усиливает социальный статус и открывает дополнительные ветки общения с канадскими NPC."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "observe_polite_interactions", "text": "Наблюдать повседневные взаимодействия в общественных пространствах и фиксировать паттерны вежливости", "type": "interact", "target": "public_spaces_toronto", "count": 1, "optional": false}, {"id": "understand_sorry_culture", "text": "Изучить канадскую культуру извинений и понять её культурный код", "type": "interact", "target": "cultural_observation", "count": 1, "optional": false}, {"id": "experiment_with_norms", "text": "Экспериментировать с нарушением норм и наблюдать реакцию NPC", "type": "choice", "target": "norm_experimentation", "count": 1, "optional": true}], "rewards": {"experience": 800, "money": 0, "items": [{"item_id": "achievement_polite_citizen", "quantity": 1}], "reputation": {"Toronto_Social_Community": 15}, "unlocks": ["canadian_npc_dialogues", "social_quests_toronto"]}, "branches": [{"branch_id": "cultural_integration_path", "description": "Успешно интегрироваться в канадский культурный код", "consequences": {"skills": {"social": 20, "diplomacy": 15}, "reputation": {"Toronto_Social_Community": 25}}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест фокусируется на канадской культуре вежливости. Игрок проводит серию наблюдений, анализирует частоту использования\\nслова «sorry», изучает очереди и поведение в публичных местах, после чего адаптирует собственную речь.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Наблюдение за столкновениями и извинениями\\nЛокация: Торонто, общественные места (улицы, метро, магазины)\\nОписание: Игрок наблюдает за столкновениями и фиксирует количество извинений с обеих сторон. В Торонто слово «sorry» звучит каждые несколько минут, даже без прямой вины.\\nЦели:\\n  - Найти общественные места (улицы, метро, магазины)\\n  - Наблюдать за столкновениями между людьми\\n  - Фиксировать количество извинений с обеих сторон\\n  - Изучить канадскую культуру вежливости\\n  - Записать наблюдения в дневник\\n  - Проверка навыков: Observation 0.3+ для наблюдений, Social 0.3+ для понимания социальных паттернов, Awareness 0.3+ для изучения культуры\\n\\nЭтап 2: Анализ поведения в очередях и у дверей\\nЛокация: Торонто, общественные места (магазины, метро, офисы)\\nОписание: Игрок анализирует, как канадцы придерживают двери и взаимодействуют в очередях. Порядок в очередях и придерживание дверей считаются обязательными социальными ритуалами.\\nЦели:\\n  - Найти общественные места с очередями (магазины, метро, офисы)\\n  - Наблюдать за поведением в очередях\\n  - Изучить, как канадцы придерживают двери\\n  - Фиксировать паттерны вежливости\\n  - Записать наблюдения в дневник\\n  - Проверка навыков: Observation 0.3+ для наблюдений, Social 0.3+ для понимания этикета, Cultural 0.3+ для изучения культуры\\n\\nЭтап 3: Эксперимент с нарушением этикета\\nЛокация: Торонто, общественные места (магазины, метро, кафе)\\nОписание: Игрок проводит эксперимент с нарушением этикета и оценивает реакцию NPC. Искренность вежливости является отличительной чертой канадской идентичности.\\nЦели:\\n  - Выбрать место для эксперимента (магазин, метро, кафе)\\n  - Нарушить этикет (не извиниться, не придержать дверь, не соблюсти очередь)\\n  - Оценить реакцию NPC\\n  - Изучить последствия нарушения норм\\n  - Записать результаты эксперимента в дневник\\n  - Проверка навыков: Social 0.3+ для оценки реакции, Ethics 0.3+ для понимания этики, Awareness 0.3+ для анализа последствий\\n\\nЭтап 4: Принятие культурных норм и внедрение «sorry»\\nЛокация: Торонто, общественные места\\nОписание: Игрок принимает культурные нормы и внедряет «sorry» в собственные диалоги. Игрок учится интегрироваться в культурный код Канады.\\nЦели:\\n  - Принять культурные нормы\\n  - Начать использовать «sorry» в диалогах\\n  - Применить вежливость в повседневных взаимодействиях\\n  - Придерживать двери для других\\n  - Соблюдать порядок в очередях\\n  - Записать опыт интеграции в дневник\\n  - Проверка навыков: Social 0.4+ для применения норм, Cultural 0.3+ для интеграции, Adaptation 0.3+ для адаптации\\n\\nЭтап 5: Получение положительной обратной связи\\nЛокация: Торонто, общественные места, встречи с местными жителями\\nОписание: Игрок завершает квест, получив положительную обратную связь от местных. Игрок получает признание за интеграцию в канадскую культуру.\\nЦели:\\n  - Взаимодействовать с местными жителями\\n  - Получить положительную обратную связь о вежливости\\n  - Продемонстрировать понимание канадской культуры\\n  - Завершить квест\\n  - Проверка навыков: Social 0.4+ для взаимодействия, Cultural 0.4+ для демонстрации понимания, Rapport 0.3+ для установления связи\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml", "mechanics/social/social-interactions.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- В Торонто слово «sorry» звучит каждые несколько минут, даже без прямой вины.\\n- Порядок в очередях и придерживание дверей считаются обязательными социальными ритуалами.\\n- Искренность вежливости является отличительной чертой канадской идентичности.\\n", "mechanics_links": [], "assets": []}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Местный житель Торонто (Сара)\\nДиалог 1 (при наблюдении):\\n  - Сара: \\"Sorry! Oh, excuse me! I''m so sorry!\\"\\n  - Игрок: \\"No problem at all.\\"\\n  - Сара: \\"Thank you! Sorry again!\\"\\n  - Игрок: \\"Why do you say sorry so much?\\"\\n  - Сара: \\"I don''t know, it''s just... polite? It''s a Canadian thing, I guess. We apologize even when it''s not our fault. It''s just how we are!\\"\\n\\nРаботник метро (Майк)\\nДиалог 2 (в очереди):\\n  - Майк: \\"Sorry, excuse me, sorry...\\"\\n  - Игрок: \\"After you!\\"\\n  - Майк: \\"No, no, after you! Sorry!\\"\\n  - Игрок: \\"Why do Canadians apologize so much?\\"\\n  - Майк: \\"It''s our way of showing respect, I think. Even if we''re not at fault, we say sorry. It''s about being considerate, showing empathy. It''s just... Canadian.\\"\\n\\nБариста в кафе (Эмили)\\nДиалог 3 (при нарушении этикета):\\n  - Эмили: \\"Um... excuse me, but... were you in line?\\"\\n  - Игрок: \\"Oh, I just went ahead.\\"\\n  - Эмили: \\"Oh, I see... sorry for asking, but... usually we wait in line here. It''s just... polite?\\"\\n  - Игрок: \\"I understand.\\"\\n  - Эмили: \\"Thank you for understanding! Sorry if I sounded rude!\\"\\n\\nСтудент университета (Джейк)\\nДиалог 4 (при принятии норм):\\n  - Джейк: \\"Sorry, can I help you?\\"\\n  - Игрок: \\"Sorry, I''m just observing. Sorry for bothering you.\\"\\n  - Джейк: \\"No, no! Sorry, you''re not bothering me at all! Sorry for asking!\\"\\n  - Игрок: \\"Sorry, I just wanted to learn about Canadian culture.\\"\\n  - Джейк: \\"Oh, sorry! I understand! Sorry for the confusion! You''re doing great! Sorry!\\"\\n\\nМестный житель (Анна)\\nДиалог 5 (положительная обратная связь):\\n  - Анна: \\"You''re doing great! You''re really integrating well into Canadian culture!\\"\\n  - Игрок: \\"Thank you! Sorry, I''m still learning.\\"\\n  - Анна: \\"No, don''t apologize! You''re doing wonderfully! The fact that you''re saying ''sorry'' shows you understand our culture. It''s about empathy, respect, consideration. You''re becoming a true Canadian!\\"\\n  - Игрок: \\"Thank you! Sorry for taking your time!\\"\\n  - Анна: \\"No problem at all! Sorry, I should let you go! Sorry!\\"\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Подход к наблюдениям\\nУсловие: При начале квеста\\nВарианты:\\n  - Интенсивное наблюдение: детально изучать каждое взаимодействие, больше времени, но больше информации, разблокируется дополнительная информация о культуре\\n  - Быстрое наблюдение: быстро обходить места, меньше времени, но стандартная информация\\n  - Сбалансированный подход: оптимальный баланс\\n\\nВыбор 2: Тип эксперимента (критический выбор)\\nУсловие: При проведении эксперимента с нарушением этикета\\nВарианты:\\n  - Не извиниться при столкновении: -5 репутация Toronto Social Community, разблокируется информация о реакции на грубость\\n  - Не придержать дверь: -3 репутация, разблокируется информация о реакции н