INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-toronto-2029-bilingualism', 'Торонто 2020-2029 — Билингвизм Канады', 'Игрок исследует городские пространства, встречает франкофонов и анализирует двуязычную инфраструктуру.', 'side', 4, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_bilingual_infrastructure", "text": "Исследовать городские пространства и проанализировать двуязычную инфраструктуру", "type": "interact", "target": "toronto_bilingual_signs", "count": 1, "optional": false}, {"id": "meet_francophones", "text": "Встретить франкофонов в Торонто и взаимодействовать с ними", "type": "interact", "target": "francophone_community_toronto", "count": 1, "optional": false}, {"id": "understand_bilingualism", "text": "Понять, как сосуществуют английский и французский языки в Канаде", "type": "interact", "target": "bilingual_cultural_study", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "money": 0, "items": [{"item_id": "achievement_bilingual_citizen", "quantity": 1}], "reputation": {"Toronto_Cultural_Community": 15}, "unlocks": ["francophone_dialogues", "cultural_quests_toronto"]}'::jsonb, '[{"branch_id": "language_master_path", "description": "Овладеть двуязычными навыками", "consequences": {"skills": {"cultural": 20, "language": 25}, "reputation": {"Toronto_Cultural_Community": 25}}}]'::jsonb, '{"metadata": {"id": "quest-toronto-2029-bilingualism", "title": "Торонто 2020-2029 — Билингвизм Канады", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-23T02:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["toronto", "language", "culture"], "topics": ["national_identity", "social_dynamics"], "related_systems": ["gameplay-service", "quest-service", "character-service", "economy-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1056", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1056", "relation": "migrated_to", "migrated_at": "2025-11-23T02:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/toronto/2020-2029/quest-009-bilingualism.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурная линия Канады не раскрывала влияние официального билингвизма на жителей Торонто.", "goal": "Показать, как сосуществуют английский и французский языки, и дать игроку опыт переключения между ними.", "essence": "Игрок исследует городские пространства, встречает франкофонов и анализирует двуязычную инфраструктуру.", "key_points": ["Канада имеет два официальных языка, что отражается в документах и городской навигации.", "Франкофоны присутствуют в Торонто, несмотря на доминирование английского языка.", "Двуязычные вывески и приветствия формируют повседневную идентичность."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_bilingual_infrastructure", "text": "Исследовать городские пространства и проанализировать двуязычную инфраструктуру", "type": "interact", "target": "toronto_bilingual_signs", "count": 1, "optional": false}, {"id": "meet_francophones", "text": "Встретить франкофонов в Торонто и взаимодействовать с ними", "type": "interact", "target": "francophone_community_toronto", "count": 1, "optional": false}, {"id": "understand_bilingualism", "text": "Понять, как сосуществуют английский и французский языки в Канаде", "type": "interact", "target": "bilingual_cultural_study", "count": 1, "optional": false}], "rewards": {"experience": 1000, "money": 0, "items": [{"item_id": "achievement_bilingual_citizen", "quantity": 1}], "reputation": {"Toronto_Cultural_Community": 15}, "unlocks": ["francophone_dialogues", "cultural_quests_toronto"]}, "branches": [{"branch_id": "language_master_path", "description": "Овладеть двуязычными навыками", "consequences": {"skills": {"cultural": 20, "language": 25}, "reputation": {"Toronto_Cultural_Community": 25}}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с канадской системой языковой дуальности: от надписей на двух языках до культурных практик\\nобщения в Торонто, связывая мегаполис с наследием Квебека.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить районы с двуязычными вывесками и зафиксировать различия между официальными и частными надписями.\\n2. Встретиться с франкофонным сообществом, отработать приветствия «Bonjour/Hello».\\n3. Исследовать административные здания и общественный транспорт, проверить наличие двуязычных объявлений.\\n4. Попробовать вести диалог на французском, оценивая реакцию NPC и влияние на репутацию.\\n5. Сделать вывод о роли билингвизма в национальной идентичности.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "В стране два официальных языка — английский и французский. Торонто преимущественно англоязычный, однако двуязычные\\nстандарты формируют визуальную среду города и связывают его с франкофонными регионами.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Атрибут «Культура»: +20\\n- Ачивка: «Билингвист»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест включает образовательные и социальные механики, открывает новые диалоговые варианты и повышает репутацию среди\\nфранкофонных фракций.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с культурной линией Канады, сюжетами о Квебеке и миссиями по межрегиональному сотрудничеству.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1056, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Билингвизм Канады» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

