INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-astana-modern-nomads', 'Астана 2020-2029 — «Современные кочевники»', 'Команда героев погружается в жизнь кочевого аула с солнечными юртами, спутниковым интернетом и беркутами — прошлое и будущее в одном лагере.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_nomad_camp", "text": "Выехать из Астаны в степь и найти лагерь современных кочевников", "type": "interact", "target": "nomad_camp_finding", "count": 1, "optional": false}, {"id": "meet_elder", "text": "Встретиться со старейшиной и пройти обряд знакомства", "type": "interact", "target": "nomad_elder_meeting", "count": 1, "optional": false}, {"id": "help_infrastructure", "text": "Помочь починить метеостанцию, обновить сетевой узел и настроить канал связи", "type": "interact", "target": "nomad_infrastructure_help", "count": 1, "optional": false}, {"id": "participate_traditions", "text": "Участвовать в традициях: кумыс, охота с беркутом и вечерний песенный круг", "type": "interact", "target": "nomad_traditions_participation", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": 0, "reputation": {"culture": 15}, "items": [], "unlocks": {"achievements": ["Друг кочевников"], "flags": ["modern_nomads_faction_unlocked"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-astana-modern-nomads", "title": "Астана 2020-2029 — «Современные кочевники»", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["astana", "nomad", "culture"], "topics": ["hybrid-tradition", "faction-life"], "related_systems": ["narrative-service", "faction-system", "travel-system"], "related_documents": [{"id": "canon-quest-almaty-nomad-culture", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/astana/2020-2029/quest-006-modern-nomads.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Первичное описание современных кочевников не фиксировало баланс традиций и высоких технологий и не подключало фракционную прогрессию.", "goal": "Структурировать квест, где игрок знакомится с автономным лагерем, помогает наладить инфраструктуру и получает доступ к знаниям степи.", "essence": "Команда героев погружается в жизнь кочевого аула с солнечными юртами, спутниковым интернетом и беркутами — прошлое и будущее в одном лагере.", "key_points": ["Вступление во фракцию современных номадов через ритуалы и техническую поддержку лагеря.", "Показ адаптации языка, кухни и охоты к цифровому миру.", "Награды усиливают культурные навыки и открывают мобильные сервисы в степи."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_nomad_camp", "text": "Выехать из Астаны в степь и найти лагерь современных кочевников", "type": "interact", "target": "nomad_camp_finding", "count": 1, "optional": false}, {"id": "meet_elder", "text": "Встретиться со старейшиной и пройти обряд знакомства", "type": "interact", "target": "nomad_elder_meeting", "count": 1, "optional": false}, {"id": "help_infrastructure", "text": "Помочь починить метеостанцию, обновить сетевой узел и настроить канал связи", "type": "interact", "target": "nomad_infrastructure_help", "count": 1, "optional": false}, {"id": "participate_traditions", "text": "Участвовать в традициях: кумыс, охота с беркутом и вечерний песенный круг", "type": "interact", "target": "nomad_traditions_participation", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": 0, "reputation": {"culture": 15}, "items": [], "unlocks": {"achievements": ["Друг кочевников"], "flags": ["modern_nomads_faction_unlocked"]}}, "branches": []}, "content": {"sections": [{"id": "arrival", "title": "Прибытие в лагерь", "body": "Игрок выезжает из Астаны в степь и находит лагерь, где юрты питаются от солнечных панелей. Старейшина объясняет\\nкодекс кочевников и предлагает пройти обряд знакомства, включающий обмен подарками и короткий рассказ о роде.\\n", "mechanics_links": [], "assets": []}, {"id": "tradition_and_network", "title": "Традиции и инфраструктура", "body": "- Игрок помогает угощать кумысом, наблюдает охоту с беркутом и участвует в вечернем песенном круге.\\n- Параллельно требуется починить метеостанцию, обновить сетевой узел и настроить безопасный канал связи с городом.\\n- Каждая задача открывает новые диалоги о сохранении культуры и развитии автономных поселений.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Награды и влияние", "body": "- 2 000 XP, +15 к показателю «Культура» и достижение «Друг кочевников».\\n- Доступ к фракционным заданиям по логистике степи и уникальным крафтовым схемам (мобильная юрта, модификаторы для транспорта).\\n- Возможность вызвать кочевой караван для временного лагеря в других регионах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Современные номады", "definition": "Казахские кочевники, совмещающие традиционный быт с автономными энергетическими и коммуникационными технологиями."}], "references": [{"title": "Modern Nomads of Kazakhstan", "link": "https://www.aljazeera.com/features/2021/6/30/modern-nomads-in-kazakhstan"}], "decisions": []}, "implementation": {"github_issue": 913, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 913."}, {"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Современные кочевники» в формат knowledge-entry с фракционным фокусом."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

