INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-astana-palace-of-peace', 'Астана 2020-2029 — «Дворец мира и согласия»', 'Игрок проходит через ключевые уровни Дворца мира и согласия, участвует в сессии лидеров и фиксирует панораму Астаны.', 'side', 5, 20, '[{"type": "location", "value": "astana"}]'::jsonb, '[{"id": "explore_palace", "type": "explore", "description": "Исследовать архитектуру Дворца мира и согласия", "required": true}, {"id": "attend_congress", "type": "interact", "description": "Участвовать в сессии Конгресса мировых религий", "required": true}, {"id": "view_panorama", "type": "interact", "description": "Зафиксировать панораму Астаны со смотровой площадки", "required": true}]'::jsonb, '{"experience": 1000, "money": {"type": "eddies", "value": -15}, "reputation": {"culture": 10}, "unlocks": {"achievements": [{"id": "peacemaker", "name": "Миротворец"}], "flags": ["interfaith_events_unlocked"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-astana-palace-of-peace", "title": "Астана 2020-2029 — «Дворец мира и согласия»", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["astana", "architecture", "interfaith"], "topics": ["tolerance", "cultural-diplomacy"], "related_systems": ["narrative-service", "diplomacy-system", "tourism-system"], "related_documents": [{"id": "canon-quest-astana-baiterek-tower", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/astana/2020-2029/quest-007-palace-of-peace.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исходное описание не показывало, как пирамида Фостера поддерживает межрелигиозный диалог и дипломатические инициативы столицы.", "goal": "Создать экскурсионный квест, раскрывающий архитектурную символику, работу Конгресса мировых религий и культурные программы внутри дворца.", "essence": "Игрок проходит через ключевые уровни Дворца мира и согласия, участвует в сессии лидеров и фиксирует панораму Астаны.", "key_points": ["Архитектура и освещение подчеркивают идею мира между религиями.", "Конгресс предоставляет временные бонусы к дипломатическим проверкам.", "Награды усиливают культурный рейтинг и открывают межфракционные события."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": 20, "requirements": [{"type": "location", "value": "astana"}], "objectives": [{"id": "explore_palace", "type": "explore", "description": "Исследовать архитектуру Дворца мира и согласия", "required": true}, {"id": "attend_congress", "type": "interact", "description": "Участвовать в сессии Конгресса мировых религий", "required": true}, {"id": "view_panorama", "type": "interact", "description": "Зафиксировать панораму Астаны со смотровой площадки", "required": true}], "rewards": {"experience": 1000, "money": {"type": "eddies", "value": -15}, "reputation": {"culture": 10}, "unlocks": {"achievements": [{"id": "peacemaker", "name": "Миротворец"}], "flags": ["interfaith_events_unlocked"]}}, "branches": []}, "content": {"sections": [{"id": "architecture", "title": "Архитектурное вступление", "body": "Экскурсия начинается у входа в пирамиду. Игрок узнаёт о геометрии здания, системе вентиляции, символике витражей и о том,\\nкак Норман Фостер адаптировал традицию юрты к стеклу и металлу. Проводник выдаёт аудиогид с историей форума.\\n", "mechanics_links": [], "assets": []}, {"id": "congress", "title": "Конгресс мировых религий", "body": "В верхнем зале проходит заседание лидеров конфессий. Игрок может участвовать в диалогах, задавать вопросы и\\nполучить временные баффы к дипломатии. Параллельно отображается интерактивная хроника конгрессов 2003–2090 годов.\\n", "mechanics_links": [], "assets": []}, {"id": "panorama", "title": "Финал экскурсии", "body": "Завершение происходит на смотровой площадке с видом на Астану и Есиль. Игрок фиксирует культурную память, получает\\n1 000 XP, -15 едди (стоимость экскурсии) и +10 к показателю «Культура», а также ачивку «Миротворец».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Конгресс лидеров мировых и традиционных религий", "definition": "Международный форум, который с 2003 года проводится в столице Казахстана для развития межрелигиозного диалога."}], "references": [{"title": "Palace of Peace and Reconciliation", "link": "https://www.visitkazakhstan.kz/en/guide/places/view/163/"}], "decisions": []}, "implementation": {"github_issue": 914, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Дворец мира и согласия» в knowledge-entry с фокусом на культурную дипломатию."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Обновлен github_issue на 914."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

