INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-stanley-park', 'Ванкувер 2020-2029 — Стэнли Парк', 'Игрок совершает круг по Seawall, изучает тотемные столбы и фиксирует панорамы на горы и океан.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-stanley-park", "title": "Ванкувер 2020-2029 — Стэнли Парк", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "nature", "urban_parks"], "topics": ["indigenous_culture", "eco_tourism"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-001-stanley-park.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природная линия Ванкувера не освещала значимость Stanley Park и его связь с наследием First Nations.", "goal": "Показать сочетание городского ландшафта, океана и культурных артефактов в крупнейшем парке города.", "essence": "Игрок совершает круг по Seawall, изучает тотемные столбы и фиксирует панорамы на горы и океан.", "key_points": ["Stanley Park — один из крупнейших городских парков мира площадью более 400 гектаров.", "Seawall протяжённостью 9 км обеспечивает уникальный маршрут вокруг парка.", "Тотемные столбы отражают наследие народов First Nations."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока через ключевые точки Stanley Park, демонстрируя гармонию природы и городской среды, а также\\nнаследие коренных народов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Отправиться на велосипедную или пешеходную прогулку по Seawall, собрать данные о пейзажах.\\n2. Посетить площадку с тотемными столбами, записать историю каждой фигуры.\\n3. Исследовать Lost Lagoon и наблюдать за фауной.\\n4. Зафиксировать панорамы North Shore, Тихого океана и центра Ванкувера.\\n5. Подготовить туристический путеводитель о зелёных зонах мегаполиса.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Парк площадью более 400 гектаров представляет собой естественный лес, окружённый Seawall. Тотемы First Nations\\nохраняются как культурное наследие, а виды на горы и океан делают локацию культовой.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 200 XP\\n- Атрибут «Природа»: +20\\n- Ачивка: «Хранитель парка»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует туристические и природные механики, предоставляя бонусы к исследованию городских парков и доступ\\nк сюжетам коренных народов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с природной линией Ванкувера, заданиями о культурном наследии пропусков First Nations и сюжетами о городской экологии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1041, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Стэнли Парк» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

