INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-astana-kazakh-language', 'Астана 2020-2029 — «Казахский язык»', 'В Астане игрок проходит интенсивный курс казахского: от истории языка до практики с носителями и экзаменационной комиссии.
