INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-007-architecture-tour', 'Чикаго — Архитектурный тур по реке', 'Игрок участвует в речном круизе Chicago Architecture Center, узнаёт историю небоскрёбов и открывает архитектурную линейку города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-007-architecture-tour", "title": "Чикаго — Архитектурный тур по реке", "document_type": "canon", "category": "lore", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-09T11:23:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "architecture", "tourism"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "tourism-service", "events-service"], "related_documents": [{"id": "questline-chicago-architecture", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-007-architecture-tour.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Архитектурный круиз описан текстовой заметкой и не раскрывает образовательные ветки Millennium Park и реки Чикаго.", "goal": "Зафиксировать ключевые точки маршрута, погодные ветвления и образовательную ценность туров.", "essence": "Игрок участвует в речном круизе Chicago Architecture Center, узнаёт историю небоскрёбов и открывает архитектурную линейку города.", "key_points": ["Включены Willis Tower, Marina City, Wrigley Building и другие знаковые здания.", "Гиды рассказывают про Chicago School и влияние пожара 1871 года.", "Квест подключён к культурным событиям и туристической аналитике."]}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Чикаго — родина небоскрёбов и Chicago School. Архитектурный круиз по реке даёт игрокам концентрированный взгляд на историю стального каркаса, восстановление после пожара и современные иконы skyline.\\n", "mechanics_links": ["mechanics/social/tourism-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Архитектурные точки", "body": "- Willis Tower, Trump Tower, Marina City, Wrigley Building и другие объекты вдоль реки.\\n- Архитекторы Луис Салливен и Фрэнк Ллойд Райт как ключевые имена.\\n- Маршрут адаптируется под погодные условия: солнечный день, дождь, вечерняя подсветка.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-architecture.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы круиза", "body": "1. Посадка у Chicago Architecture Center, выбор варианта тура (дневной/ночной/приватный).\\n2. Прослушивание истории зданий, ранжированных по эпохам (до пожара, Chicago School, модернизм).\\n3. Регистрация фотосцен и заметок в дневнике игрока.\\n4. Получение маршрута для самостоятельного обхода Millennium Park.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 500 XP, −45 валюты, показатель «Культура» +20.\\n- Ачивка «Знаток Архитектуры».\\n- Разблокировка дополнительных экскурсий и скидок на культурные события.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Квест развивает архитектурную ветку, поддерживает мероприятия CAC и привязывает образовательные KPI к туристическому потоку. Может запускать вечерние концерты и лекции об устойчивом строительстве.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-007"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Описан архитектурный круиз как часть образовательной петли Чикаго."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

