-- Quest: quest-vancouver-2029-rain-city
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-rain-city', 'Ванкувер 2020-2029 — Дождливый город', 'Игрок проживает неделю дождливого сезона, наблюдает поведение NPC и поддерживает эмоциональное состояние.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-rain-city", "title": "Ванкувер 2020-2029 — Дождливый город", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "climate", "lifestyle"], "topics": ["weather_patterns", "mental_health"], "related_systems": ["narrative-service", "emotion-system"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-002-rain-city.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Климатическая линия Ванкувера не описывала влияние постоянных дождей на жителей и настроение.", "goal": "Передать образ «Raincouver», показать адаптацию горожан и управление эффектами SAD.", "essence": "Игрок проживает неделю дождливого сезона, наблюдает поведение NPC и поддерживает эмоциональное состояние.", "key_points": ["В Ванкувере более 170 дождливых дней в году, особенно с октября по март.", "Местные жители предпочитают капюшоны вместо зонтов и компенсируют серость активностями.", "Постоянная морось может вызывать сезонное аффективное расстройство."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест моделирует жизнь в «Raincouver»: игрок фиксирует погодные паттерны, поведение жителей и способы борьбы с низким\\nнастроением в течение дождливой недели.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Провести наблюдения за погодой в течение недели, отмечая интенсивность осадков.\\n2. Отслеживать маршруты горожан, которые избегают зонтов и предпочитают капюшоны.\\n3. Посещать кафе и культурные пространства, где жители пережидают дождь.\\n4. Следить за уровнем настроения и предотвращать SAD с помощью светотерапии и тренировок.\\n5. Подготовить отчёт о влиянии климата на экономику и социальные связи.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Прозвище «Raincouver» подчеркивает количество дождливых дней. Региональный климат мягкий, но отличается постоянной моросью,\\nблагодаря чему город остаётся зелёным круглый год.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Атрибут «Выносливость»: +15\\n- Дебафф: -10% настроения (SAD), пока не применены восстановительные действия\\n- Ачивка: «Выживший в Raincouver»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует климатические и эмоциональные механики, расширяя доступ к погодным модификаторам и социальным событиям\\nдождливого сезона.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с климатической линией Ванкувера и сюжетами о ментальном здоровье жителей западного побережья.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1042, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Дождливый город» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

