INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-mountains-ocean', 'Ванкувер 2020-2029 — Горы и океан за один день', 'Игрок проводит день, совмещая катание на Grouse Mountain и отдых в English Bay.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-mountains-ocean", "title": "Ванкувер 2020-2029 — Горы и океан за один день", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "geography", "tourism"], "topics": ["outdoor_activity", "urban_identity"], "related_systems": ["narrative-service", "world-state-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-003-mountains-ocean.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Природная линия Ванкувера не демонстрировала уникальное сочетание гор и океана в пределах одного дня.", "goal": "Подчеркнуть туристический контраст — лыжи утром, пляж вечером — и его воздействие на городскую идентичность.", "essence": "Игрок проводит день, совмещая катание на Grouse Mountain и отдых в English Bay.", "key_points": ["До лыжных трасс Grouse Mountain всего 15 минут от центра города.", "Пляжи English Bay доступны сразу после спуска с гор.", "География формирует брендинг Ванкувера как города крайностей."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест демонстрирует контрастные активности Ванкувера, заставляя игрока планировать логистику между горами и океаном,\\nфиксируя эмоциональные и визуальные впечатления.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Утром добраться до Grouse Mountain, подняться на вершину и зафиксировать виды на город.\\n2. Провести серию лыжных заездов, собрать данные о погоде и состоянии трасс.\\n3. Спуститься в город, оценить транспортную доступность и стоимость.\\n4. Отправиться на English Bay, завершить день на пляже и задокументировать закат.\\n5. Подготовить отчёт о туристическом потенциале формата «горы + океан».\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ванкувер расположен между горами North Shore и заливом Джорджия, что создаёт уникальные условия для экстремальных\\nи морских активностей в пределах одного мегаполиса.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP\\n- Расходы: 80 единиц валюты\\n- Атрибут «Активность»: +30\\n- Ачивка: «Горы и океан»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест сочетает природные и туристические механики, предоставляя бонусы к выносливости и открывая элитные активити\\nВанкувера.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с природной линией Ванкувера, заданиями о климатических контрастах и сюжетами о позиционировании города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1043, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Горы и океан за один день» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

