INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-asian-influence', 'Ванкувер 2020-2029 — Азиатское влияние', 'Игрок исследует азиатские кварталы, дегустирует кухню и документирует визуальную двуязычную среду.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-asian-influence", "title": "Ванкувер 2020-2029 — Азиатское влияние", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "multiculturalism", "asian_culture"], "topics": ["migration", "cultural_identity"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-004-asian-influence.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальная линия Ванкувера не показывала масштаб азиатского влияния и трансформацию городских районов.", "goal": "Погрузить игрока в районы Ричмонда и Чайна-тауна, подчеркнув мультикультурную идентичность региона.", "essence": "Игрок исследует азиатские кварталы, дегустирует кухню и документирует визуальную двуязычную среду.", "key_points": ["Доля жителей азиатского происхождения достигает 45%, а Ричмонд населен на 70% азиатами.", "Прозвище «Hongcouver» отражает миграцию из Гонконга и Китая.", "Азиатская гастрономия и вывески формируют узнаваемость города."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с культурными артефактами азиатских общин Ванкувера, от семейных ресторанов до праздничных\\nмероприятий, формируя карту влияний в мегаполисе.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить пригород Ричмонд, зафиксировать двуязычные вывески и уличные рынки.\\n2. Исследовать Chinatown, изучить исторические здания и культурные центры.\\n3. Составить гастрономический маршрут: димсам, суши, вьетнамский phở.\\n4. Интервьюировать местных жителей о миграции и адаптации.\\n5. Подготовить отчёт о влиянии АТР на экономику и быт Ванкувера.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ванкувер — один из самых мультикультурных городов Северной Америки. Ричмонд славится азиатскими супермаркетами,\\nа прозвище «Hongcouver» отражает волну миграции из Гонконга в 1990-х и 2010-х годах.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 30 единиц валюты\\n- Атрибут «Культура»: +25\\n- Ачивка: «Азиатский ванкуверец»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует культурные и социальные механики, повышает репутацию среди торговых гильдий и открывает события\\nдиаспор.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с социальной линией Ванкувера, сюжетами об эмиграции и миссиями приграничного сотрудничества с АТР.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1044, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Азиатское влияние» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

