INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-baku-flame-towers', 'Баку — Пламенные башни', 'Игрок наблюдает контраст современного Баку и Старого города, поднимаясь на смотровую площадку во время ночного LED-шоу.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-baku-flame-towers", "title": "Баку — Пламенные башни", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T02:10:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T02:10:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["baku", "architecture", "tourism"], "topics": ["regional-content", "sightseeing"], "related_systems": ["exploration-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/baku/2020-2029/quest-001-flame-towers.md", "visibility": "internal", "audience": ["lore", "quest-design", "world-building"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о Flame Towers существовал в Markdown и не был интегрирован в структуру знаний.", "goal": "Описать туристический маршрут по Пламенным башням с акцентом на визуальные эффекты и культурный контекст.", "essence": "Игрок наблюдает контраст современного Баку и Старого города, поднимаясь на смотровую площадку во время ночного LED-шоу.", "key_points": ["Зафиксированы этапы дня/ночи и визуальные эффекты башен.", "Добавлены связи с архитектурной линией Баку и mood-system.", "Прописаны награды и достижения для туристических веток."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 800 XP, -20 едди (расходы), +10 эстетики. Ачивка «Хранитель Пламени».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть к Flame Towers днём и изучить архитектуру.\\n2. Дождаться вечернего LED-шоу на фасадах башен.\\n3. Подняться на смотровую площадку ночью и получить панораму Баку и Каспия.\\n4. Зафиксировать контраст с историческим центром внизу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Башни высотой 190 м символизируют «Землю огня». Ночные LED-анимации усиливают атмосферу неонового Баку.\\nУпомянуть соседство со Старым городом и нефтяным наследием региона.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Эксплоринговые задачи для exploration-system, настроение «Вдохновение» в mood-system, архитектурная линия Баку.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 800 XP, -20 едди (поездка и билеты), +10 эстетики, ачивку «Хранитель Пламени».\\nРазблокируется доступ к квестам о современной архитектуре Баку.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия квеста Flame Towers в YAML 1.1.0, добавлены системные крючки и награды."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

