INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-real-estate-crisis', 'Ванкувер 2020-2029 — Кризис недвижимости', 'Игрок анализирует пустующие особняки, интервьюирует местных жителей и принимает решение о дальнейших действиях.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-real-estate-crisis", "title": "Ванкувер 2020-2029 — Кризис недвижимости", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "real_estate", "economy"], "topics": ["housing", "social_inequality"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-005-real-estate-crisis.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экономическая линия Ванкувера не раскрывала причины и последствия роста цен на жильё.", "goal": "Исследовать рынок недвижимости, показать влияние внешних инвестиций и социальную напряжённость.", "essence": "Игрок анализирует пустующие особняки, интервьюирует местных жителей и принимает решение о дальнейших действиях.", "key_points": ["Средняя стоимость дома превышает 1,5–2 млн долларов; квартиры стоят от 700 тыс.", "Потоки капитала из Азии приводят к спекуляциям и пустующим домам.", "Молодые профессионалы сталкиваются с недоступностью жилья."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает игрока в кризис недвижимости Ванкувера, позволяя изучить статистику цен, район West Side и влияние\\nиностранных инвесторов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать информацию о стоимости домов и квартир, сравнить с доходами населения.\\n2. Посетить элитные районы и зафиксировать пустующие особняки.\\n3. Встретиться с представителями молодых семей и записать их истории.\\n4. Проанализировать данные об иностранных инвестициях и политиках контроля.\\n5. Принять решение: продвигать реформы, инвестировать лично или покинуть регион.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ванкувер входит в число самых дорогих рынков жилья в мире. Факторы включают ограниченную географию, спрос из Китая\\nи привлекательность города для капитала.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 4 000 XP\\n- Атрибут «Экономика»: +30\\n- Ачивка: «Знаток кризиса»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует экономические и социальные механики, влияя на стоимость аренды и отношения с гражданскими фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с экономической линией Ванкувера, миссиями о налоговой политике и сюжетами о миграции капитала.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1045, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Кризис недвижимости» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

