INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-simulation-shinto', 'Токио 2078-2093 — «Синто и Симуляция»', 'Священник открывает игроку знание о природе симуляции; через путешествие по храмам герой решает, как взаимодействовать с ками и реальностью.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-simulation-shinto", "title": "Токио 2078-2093 — «Синто и Симуляция»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_philosophy", "contact": "lore@necp.game"}], "tags": ["tokyo", "shinto", "simulation"], "topics": ["spirituality", "main-story"], "related_systems": ["narrative-service", "belief-system", "simulation-framework"], "related_documents": [{"id": "canon-quest-tokyo-offering", "relation": "precedes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2078-2093/quest-048-simulation-shinto.md", "visibility": "internal", "audience": ["lore", "narrative", "philosophy"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о синтоистских ками и симуляции был описан тезисно и не связывался с метанаративом основной кампании.", "goal": "Формализовать духовную арку, соединяющую культ ками и раскрытие симуляции, с чёткими выборами и последствиями.", "essence": "Священник открывает игроку знание о природе симуляции; через путешествие по храмам герой решает, как взаимодействовать с ками и реальностью.", "key_points": ["Исследование семи ключевых храмов Токио для понимания слоёв симуляции.", {"Три выборные линии": "принятие, отрицание или посредничество между мирами."}, "Уникальный бафф благословения ками и глубокое влияние на метакампанию."]}, "content": {"sections": [{"id": "inciting_event", "title": "Завязка", "body": "Игрок встречается с пожилым синтоистским священником в храме Мэйдзи. Он раскрывает древнее знание: ками наблюдают за\\nсимуляцией и поддерживают баланс между слоями реальности. Священник предлагает пройти ритуал и доказать готовность.\\n", "mechanics_links": [], "assets": []}, {"id": "pilgrimage", "title": "Паломничество по храмам", "body": "Игрок посещает семь ключевых храмов Токио, каждый из которых представляет аспект симуляции. В каждом храме нужно\\nвыполнить духовно-логические испытания, встретить цифровых ками и узнать, как они воспринимают людей.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Выбор пути", "body": "- **Принять:** игрок соглашается, что симуляция — полноправная реальность, усиливает свои связи с ками и получает\\n  устойчивый бафф духовности.\\n- **Отвергнуть:** герой ищет выход из симуляции, запускает ветку сопротивления и конфликта с ками.\\n- **Посредник:** устанавливает мост между симуляцией и физическим миром, становится проводником для духов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 8 000 XP и благословение ками (+20 % ко всем характеристикам на ограниченное время).\\n- Доступ к философским диалогам и скрытым логам симуляции.\\n- Влияет на дальнейшие миссии, связанные с природой реальности и религиозными фракциями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Ками", "definition": "Духи синтоизма, обитающие во всех явлениях и уровнях реальности."}], "references": [{"title": "Shinto Beliefs and Modern Interpretations", "link": "https://www.britannica.com/topic/Shinto"}], "decisions": []}, "implementation": {"github_issue": 266, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_philosophy", "changes": "Конверсия квеста «Синто и Симуляция» в knowledge-entry с выборными линиями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

