-- Quest: quest-vancouver-2029-skiing-whistler
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-skiing-whistler', 'Ванкувер 2020-2029 — Лыжи в Уистлере', 'Игрок добирается до Whistler, катается на олимпийских трассах и участвует в après-ski активности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-skiing-whistler", "title": "Ванкувер 2020-2029 — Лыжи в Уистлере", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "whistler", "winter_sports"], "topics": ["tourism", "olympic_legacy"], "related_systems": ["narrative-service", "progression-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-006-skiing-whistler.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Спортивная линия Ванкувера не описывала туристический потенциал Whistler после Олимпиады 2010.", "goal": "Показать маршрут Sea-to-Sky, инфраструктуру курорта и олимпийское наследие.", "essence": "Игрок добирается до Whistler, катается на олимпийских трассах и участвует в après-ski активности.", "key_points": ["Whistler Blackcomb остаётся крупнейшим горнолыжным курортом Северной Америки.", "Дорога Sea-to-Sky предлагает уникальные виды на океан и горы.", "Оборудование Олимпиады 2010 всё ещё используется для соревнований."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока из Ванкувера в Whistler, демонстрируя туристические и спортивные возможности региона,\\nа также рекордную канатную дорогу Peak 2 Peak.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Преодолеть маршрут по Sea-to-Sky Highway, собрать данные о панорамах и погоде.\\n2. Посетить Whistler Village, ознакомиться с инфраструктурой курорта.\\n3. Прокатиться по ключевым трассам, включая олимпийские маршруты 2010 года.\\n4. Испытать Peak 2 Peak gondola и зафиксировать высотные показатели.\\n5. Участвовать в après-ski мероприятиях, укрепляя связи с туристическими фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Whistler Blackcomb предлагает более 200 трасс, а Peak 2 Peak gondola соединяет две горы, удерживая мировые рекорды.\\nКурорт находится всего в двух часах от Ванкувера и остаётся символом зимних видов спорта Канады.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Расходы: 250 единиц валюты\\n- Атрибут «Спорт»: +30\\n- Ачивка: «Олимпийский лыжник»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест задействует спортивные и туристические механики, открывает элитные события зимних видов спорта и повышает\\nвыносливость героя.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан со спортивной линией Ванкувера, сюжетами об Олимпиаде 2010 и миссиями зимних фестивалей.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1046, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Лыжи в Уистлере» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

