-- Quest: canon-quest-tokyo-sayonara
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-sayonara', 'Токио 2078-2093 — «Сайонара, Токио»', 'Последний день в Токио — путешествие по культовым районам, завершение истории и подготовка к переезду в новый регион.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-sayonara", "title": "Токио 2078-2093 — «Сайонара, Токио»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T01:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["tokyo", "farewell", "travel"], "topics": ["epilogue", "cultural-experience"], "related_systems": ["narrative-service", "travel-system", "memory-system"], "related_documents": [{"id": "canon-quest-tokyo-shibuya-crossing", "relation": "mirrors"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/asia/tokyo/2078-2093/quest-049-sayonara-tokyo.md", "visibility": "internal", "audience": ["narrative", "live-ops", "community"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный прощальный тур по Токио не был структурирован и не связывал ключевые локации с личной аркой игрока.", "goal": "Сформировать эпилог сезона, в котором игрок прощается с городом, проходя через символические места и активируя воспоминания.", "essence": "Последний день в Токио — путешествие по культовым районам, завершение истории и подготовка к переезду в новый регион.", "key_points": ["Поэтапное посещение Сибуи, Акихабары, Токийской Башни и вида на Фудзи.", "Ностальгические сцены с NPC и запись воспоминаний в memory-system.", "Завязка для будущих сезонов через выбор «Mata aimashou»."]}, "content": {"sections": [{"id": "itinerary", "title": "Маршрут дня", "body": "1. Утренний поток на Сибуя Crossing, где фиксируются первые моменты кампании.\\n2. Полдень в Акихабаре с ретроспективой технологий и фансервисных локаций.\\n3. Закат на Токийской Башне с панорамной сценой и музыкальным сопровождением.\\n4. Ночной рамен на скрытом рынке и диалоги с близкими NPC.\\n5. Рассвет с видом на Фудзи, завершающий цикл и дающий выбор признания «до встречи».\\n", "mechanics_links": [], "assets": []}, {"id": "emotional_hooks", "title": "Эмоциональные триггеры", "body": "- **Толпы и неон:** визуальное сопровождение смены времени суток.\\n- **Звуковые дневники:** игрок записывает прощальные сообщения.\\n- **Фото-режим:** сохранение ключевых кадров и публикация в внутриигровой сети.\\n", "mechanics_links": [], "assets": []}, {"id": "system_links", "title": "Связи с системами", "body": "- **Travel-system:** трекинг маршрута на голографической карте.\\n- **Memory-system:** фиксация воспоминаний, влияющих на стартовую репутацию в следующем регионе.\\n- **Relationship hooks:** финальные диалоги с союзниками, закрывающие ветки отношений.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP и титул «Сердце Токио».\\n- Уникальные косметические предметы с символикой районов.\\n- Опция выбрать один из трех сувениров, влияющих на стартовые бонусы следующей арки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Mata aimashou", "definition": "Японское выражение «встретимся снова», использованное в финальной сцене."}], "references": [{"title": "Tokyo Farewell Guide", "link": "https://www.timeout.com/tokyo/things-to-do/farewell-tokyo"}], "decisions": []}, "implementation": {"github_issue": 266, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия квеста «Сайонара, Токио» в структурированный YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

