INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-baku-yanar-dag', 'Баку — Янар Даг (Горящая гора)', 'Игрок наблюдает вечный огонь и испытывает духовную атмосферу древнего святилища.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-baku-yanar-dag", "title": "Баку — Янар Даг (Горящая гора)", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T02:12:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T02:12:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["baku", "natural-wonders", "mysticism"], "topics": ["regional-content", "faith"], "related_systems": ["exploration-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/baku/2020-2029/quest-003-yanar-dag.md", "visibility": "internal", "audience": ["lore", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест про Янар Даг не был структурирован для lore и геймплея.", "goal": "Описать посещение вечного огня и зороастрийское наследие.", "essence": "Игрок наблюдает вечный огонь и испытывает духовную атмосферу древнего святилища.", "key_points": ["Маршрут и исторический контекст Янар Даг.", "Связь с mood-system и религиозной линией региона.", "Награды и ачивка «Огнепоклонник»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 1,500 XP, 0 едди, +10 природа. Ачивка «Огнепоклонник».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Янар Даг (25 км от Баку).\\n2. Изучить склоны, где из земли выходит природный газ и горит вечный огонь.\\n3. Узнать о поклонении зороастрийцев и истории святилища.\\n4. Провести время у огня ночью и зафиксировать атмосферу.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Янар Даг символизирует «Землю огня». Природный газ, выходящий из почвы, горит столетиями. Сайт связан с древними религиями и легендами.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "exploration-system (путешествие), mood-system (эффект «Мистический огонь»), религиозная ветка о зороастризме.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "1,500 XP, 0 едди, +10 очков природы, ачивка «Огнепоклонник». Разблокирует дополнительные задания по религиозным местам Азербайджана.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия квеста Янар Даг в YAML, добавлены системные крючки и награды."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

