INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-vancouver-2029-sushi-quality', 'Ванкувер 2020-2029 — Качество суши', 'Игрок дегустирует суши, узнаёт историю California roll и фиксирует гастрономический опыт.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-vancouver-2029-sushi-quality", "title": "Ванкувер 2020-2029 — Качество суши", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T02:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "cuisine", "sushi"], "topics": ["culinary_traditions", "cultural_identity"], "related_systems": ["narrative-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1038", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1038", "relation": "migrated_to", "migrated_at": "2025-11-23T02:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-008-sushi-quality.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия Ванкувера не показывала влияние азиатской диаспоры на качество суши.", "goal": "Исследовать легендарные рестораны, включая Tojo''s, и подчеркнуть местные ингредиенты Тихого океана.", "essence": "Игрок дегустирует суши, узнаёт историю California roll и фиксирует гастрономический опыт.", "key_points": ["Tojo''s считается местом рождения California roll.", "Свежая рыба из Тихого океана обеспечивает качество, сравнимое с Японией.", "Высокий процент азиатского населения формирует аутентичное меню."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока в гастрономическое путешествие по суши-барам Ванкувера, фокусируясь на легендарных шефах,\\nсвежей рыбе и культурных историях.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Забронировать столик в Tojo''s или другом известном суши-баре.\\n2. Изучить меню, отметить локальные ингредиенты и авторские роллы.\\n3. Узнать историю создания California roll и влияние ресторана на мировую кухню.\\n4. Сравнить вкус лосося, тунца и uni с японскими аналогами.\\n5. Подготовить обзор для туристического гида, рекомендующего гастрономические точки города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ванкувер славится доступом к свежей рыбе из Тихого океана, а высокая концентрация азиатских шеф-поваров обеспечивает\\nразнообразие стилей и техник приготовления суши.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 200 XP\\n- Расходы: 60 единиц валюты\\n- Бафф: +30% к здоровью на 10 часов\\n- Ачивка: «Суши мастер»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест добавляет кулинарные и социальные механики, повышая репутацию среди ресторанных гильдий и открывая фестивали\\nазиатской кухни.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с кулинарной линией Ванкувера, сюжетами об азиатском влиянии и миссиями гастрономического туризма.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1038, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Качество суши» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

