-- Quest: canon-quest-vancouver-2020-2029-granville-island
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vancouver-2020-2029-granville-island', 'Ванкувер 2020-2029 — «Остров Гранвилл»', 'Игрок прибывает на остров паромом Aquabus, исследует рынок и дегустирует напитки среди уличных артистов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vancouver-2020-2029-granville-island", "title": "Ванкувер 2020-2029 — «Остров Гранвилл»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "quest", "culture"], "topics": ["city_quests", "culinary_experience"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-1039", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1039", "relation": "migrated_to", "migrated_at": "2025-11-23T02:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-009-granville-island.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая линия Ванкувера не раскрывала художественную атмосферу и гастрономию Granville Island.", "goal": "Создать квест, показывающий рынок, ремёсла и микропивоварню полуострова.", "essence": "Игрок прибывает на остров паромом Aquabus, исследует рынок и дегустирует напитки среди уличных артистов.", "key_points": ["Public Market сочетает свежие продукты, морепродукты и художественные мастерские.", "Granville Island Brewing остаётся знаковым местом для дегустации локального пива.", "Уличные артисты и ремесленные лавки создают особую атмосферу квартала."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается с поездки на Aquabus. Игрок высаживается на Granville Island, чтобы познакомиться с рынком, локальными\\nремёслами и культурной средой квартала.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться на Aquabus, изучить набережную и попасть в Public Market.\\n2. Осмотреть торговые ряды с морепродуктами, фермерскими товарами и арт-галереями.\\n3. Принять участие в дегустации Granville Island Brewing и собрать отзывы пивоваров.\\n4. Зафиксировать выступления уличных артистов и атмосферу местных студий.\\n5. Подготовить путеводитель для туристов, выделив ключевые маршруты и активности.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Granville Island технически является полуостровом, соединённым с материком.\\n- Public Market объединяет производителей со всего Британской Колумбии.\\n- Aquabus обслуживает компактные маршруты по False Creek, создавая особый ритм посещения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 200 XP.\\n- Финансовые расходы: -40.\\n- Атрибут «Культура» +20.\\n- Достижение «Артист Гранвилла».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Кулинарные взаимодействия и дегустации.\\n- Социальные активности с уличными артистами и ремесленниками.\\n- Туристические маршруты с учётом транспорта Aquabus.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает культурную линию Ванкувера и открывает доступ к сюжетам о ремёслах и гастрономии побережья.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1039, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Остров Гранвилл» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

