-- Quest: canon-quest-vancouver-2020-2029-most-livable-city
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vancouver-2020-2029-most-livable-city', 'Ванкувер 2020-2029 — «Самый пригодный город»', 'Игрок исследует рейтинги, анализирует инфраструктуру и сталкивается с дилеммой affordability, принимая решение о будущем персонажа.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vancouver-2020-2029-most-livable-city", "title": "Ванкувер 2020-2029 — «Самый пригодный город»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["vancouver", "quest", "livability"], "topics": ["social-economics", "urban-planning"], "related_systems": ["narrative-service", "analytics-service"], "related_documents": [{"id": "github-issue-1040", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1040", "relation": "migrated_to", "migrated_at": "2025-11-23T02:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/vancouver/2020-2029/quest-010-most-livable-city.md", "visibility": "internal", "audience": ["concept", "narrative", "analytics"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В линейке Ванкувера отсутствовал сценарий, раскрывающий парадокс «Most Livable City vs. недоступность жилья».", "goal": "Сформировать сюжетный квест, объясняющий игроку баланс качества жизни и высокой стоимости проживания.", "essence": "Игрок исследует рейтинги, анализирует инфраструктуру и сталкивается с дилеммой affordability, принимая решение о будущем персонажа.", "key_points": ["Рейтинг «Most Livable City» строится на экологии, здравоохранении и инфраструктуре.", "Жильё и стоимость жизни превращают город в парадокс — livable, но не affordable.", "Игрок принимает решение: оставаться ли в городе при высоких расходах."]}, "content": {"sections": [{"id": "livability_index", "title": "Исследование рейтинга", "body": "Квест начинается с анализа международных индексов качества жизни. Игрок изучает данные о транспорте, климате, безопасности\\nи культурном разнообразии, сопоставляя их с личными целями персонажа.\\n", "mechanics_links": ["mechanics/analytics/city-insight-dashboard.yaml"], "assets": []}, {"id": "affordability_gap", "title": "Противоречие доступности", "body": "После вдохновения городом игрок сталкивается с проблемой стоимости: медианные цены на жильё, арендная нагрузка и\\nстатистика по кризису. Система вызывает моральную дилемму через выборы и ограничения бюджета.\\n", "mechanics_links": ["mechanics/economy/cost-of-living.yaml"], "assets": []}, {"id": "decision_point", "title": "Выбор игрока", "body": "Финальный акт — решение о том, остаться ли в городе ради качества жизни или искать альтернативу. Выбор влияет на\\nсоциальные связи персонажа и разблокирует ветки миграции в другие города.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "long_term_effects", "title": "Долгосрочные эффекты", "body": "Принятое решение влияет на экономические показатели фракции игрока, открывает новые события и взаимодействия с\\nсистемой жилья. Аналитика сохраняется для сценариев LiveOps.\\n", "mechanics_links": ["mechanics/liveops/dynamic-city-events.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1040, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Самый пригодный город» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

