INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-buenos-aires-2029-008-empanadas', 'Буэнос-Айрес — гастротур по эмпанадас', 'Игрок посещает лучшие лавки города, дегустирует начинки и помогает владельцам продвигать аутентичные рецепты.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_recommendations", "text": "Получить рекомендации местных жителей и выбрать стартовую эмпанадерию", "type": "interact", "target": "empanaderia_selection", "count": 1, "optional": false}, {"id": "learn_repulgue", "text": "Пройти мастер-класс по технике repulgue и подготовить дегустационный набор", "type": "interact", "target": "repulgue_workshop", "count": 1, "optional": false}, {"id": "choose_format", "text": "Выбрать печёный или жареный формат, влияющий на бафф и отношение гурманов", "type": "choice", "target": "empanada_format", "count": 1, "optional": false}, {"id": "conduct_tasting", "text": "Провести дегустацию для NPC и собрать отзывы, открывая дополнительные рецепты", "type": "interact", "target": "npc_tasting_session", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "money": -8, "items": [], "reputation": {"culinary": 15}, "unlocks": {"achievements": ["Эмпанада мастер"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-buenos-aires-2029-008-empanadas", "title": "Буэнос-Айрес — гастротур по эмпанадас", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:10:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["buenos-aires", "empanadas", "culinary-tour"], "topics": ["city_storylines", "food_culture"], "related_systems": ["narrative-service", "economy-service", "social-service"], "related_documents": [{"id": "quest-system", "relation": "references"}, {"id": "buenos-aires-culinary-guide", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/buenos-aires/2020-2029/quest-008-empanadas.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Базовое описание эмпанадас не раскрывает гастрономические выборы и их влияние на экономику районов.", "goal": "Сформировать квестовый маршрут по эмпанадериям, учитывая типы начинок, способы приготовления и социальные бонусы.", "essence": "Игрок посещает лучшие лавки города, дегустирует начинки и помогает владельцам продвигать аутентичные рецепты.", "key_points": ["Квест знакомит с региональными вариациями и техникой repulgue.", "Игрок выбирает между печёными и жареными версиями, влияя на статусы баффов и экономику заведений.", "Успешная дегустация открывает гастрономические события Буэнос-Айреса."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_recommendations", "text": "Получить рекомендации местных жителей и выбрать стартовую эмпанадерию", "type": "interact", "target": "empanaderia_selection", "count": 1, "optional": false}, {"id": "learn_repulgue", "text": "Пройти мастер-класс по технике repulgue и подготовить дегустационный набор", "type": "interact", "target": "repulgue_workshop", "count": 1, "optional": false}, {"id": "choose_format", "text": "Выбрать печёный или жареный формат, влияющий на бафф и отношение гурманов", "type": "choice", "target": "empanada_format", "count": 1, "optional": false}, {"id": "conduct_tasting", "text": "Провести дегустацию для NPC и собрать отзывы, открывая дополнительные рецепты", "type": "interact", "target": "npc_tasting_session", "count": 1, "optional": false}], "rewards": {"experience": 600, "money": -8, "items": [], "reputation": {"culinary": 15}, "unlocks": {"achievements": ["Эмпанада мастер"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Гастротур по эмпанадас ведёт игрока через знаковые эмпанадерии. Игрок изучает историю блюда, знакомится с локальными поварами и\\nрешает, какие рецепты продвигать, формируя репутацию гурмана и экономический вклад в район.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "filling_variations", "title": "Региональные отсылки", "body": "Empanadas — национальное блюдо Аргентины. Классические начинки: carne, pollo, jamón y queso, humita. Каждая провинция имеет\\nсобственный узор repulgue, по которому распознают начинку. Формат подачи (печёные или жареные) влияет на вкусовые характеристики и\\nвосприятие NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Получить рекомендации местных жителей и выбрать стартовую эмпанадерию.\\n2. Пройти мастер-класс по технике repulgue и подготовить дегустационный набор.\\n3. Выбрать печёный или жареный формат, влияющий на бафф и отношение гурманов.\\n4. Провести дегустацию для NPC и собрать отзывы, открывая дополнительные рецепты.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Завершение квеста приносит 600 XP, временный бафф «Nutrition Boost» (+20% HP на 6 часов) и ачивку «Эмпанада мастер». Расходы на дегустацию\\nсоставляют 8 единиц валюты, но увеличивают доход эмпанадерии и открывают скидки для будущих посетителей.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "integration", "title": "Интеграции", "body": "economy-service отслеживает рост популярности выбранных рецептов, social-service фиксирует отношение гурманских клубов, а narrative-service\\nраскрывает дополнительные гастрономические события города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1200, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Оформлен квест «Гастротур по эмпанадас» в формате YAML и описаны экономические эффекты."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

