-- Quest: quest-washington-2029-capitol-building
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-washington-2029-capitol-building', 'Вашингтон 2020-2029 — Капитолий', 'Игрок проходит экскурсию по Капитолию, наблюдает заседания и сталкивается с эхом штурма.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-washington-2029-capitol-building", "title": "Вашингтон 2020-2029 — Капитолий", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T02:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "politics", "congress"], "topics": ["democratic_institutions", "modern_history"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "governance-system", "education-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1029", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1029", "relation": "migrated_to", "migrated_at": "2025-11-23T02:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-002-capitol-building.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политическая линия Вашингтона не раскрывала роль здания Конгресса и событий 6 января 2021 года.", "goal": "Погрузить игрока в демократические процессы, показать архитектуру ротонды и напомнить о хрупкости институтов.", "essence": "Игрок проходит экскурсию по Капитолию, наблюдает заседания и сталкивается с эхом штурма.", "key_points": ["Капитолий объединяет Сенат и Палату представителей под легендарным куполом.", "Ротонда хранит фрески истории США и статуи выдающихся деятелей.", "Трагедия 6 января 2021 года остаётся частью коллективной памяти."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с архитектурой и функциями Капитолия, уделяя внимание символизму купола, работе Конгресса и\\nсовременным вызовам демократии.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Подойти к зданию Капитолия и изучить внешний облик и купол.\\n2. Пройти экскурсию по ротонде, National Statuary Hall и историческому залу Верховного суда.\\n3. Проанализировать хронику событий 6 января 2021 года через медиа-архивы и свидетельства.\\n4. Посетить галерею заседаний Конгресса, наблюдая за законодательным процессом.\\n5. Сформулировать вывод о состоянии американской демократии.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Капитолий — символ власти в округе Колумбия. Купол из чугуна возвышается над National Mall, а внутренние фрески\\nизображают ключевые моменты истории США.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 500 XP\\n- Атрибут «Демократия»: +25\\n- Ачивка: «Свидетель демократии»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует политические и исторические механики, открывает доступ к сюжетам о государственных институтах и\\nвлиянию игрока на законотворчество.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с политической линией Вашингтона, сюжетами о безопасности столицы и миссиями гражданского участия.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1029, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Капитолий» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

