INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-almaty-shymbulak-ski', 'Алматы 2020-2029 — «Лыжи Шымбулак»', 'Игрок поднимается на курорт Шымбулак, выбирает трассу и соревнуется в слаломе с видом на Алматы.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "ascend_cable_car", "text": "Подняться по канатной дороге от Медео к точке 2 200 м", "type": "travel", "target": "shymbulak_base", "count": 1, "optional": false}, {"id": "reach_summit", "text": "Дополнительный подъём на вершину 3 200 м и выбор трассы (зелёная/синяя/чёрная)", "type": "interact", "target": "shymbulak_summit", "count": 1, "optional": false}, {"id": "complete_slalom", "text": "Пройти слаломную мини-игру с учётом высотной разрежённости воздуха", "type": "interact", "target": "slalom_course", "count": 1, "optional": false}, {"id": "apres_ski", "text": "Вернуться в базовый лагерь и принять участие в apres-ski с NPC", "type": "interact", "target": "apres_ski_cafe", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": 0, "items": [{"item_id": "title_tianshan_skier", "quantity": 1}], "reputation": {}, "unlocks": ["shymbulak_extended_events"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-almaty-shymbulak-ski", "title": "Алматы 2020-2029 — «Лыжи Шымбулак»", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["almaty", "sports", "tourism"], "topics": ["regional-events", "seasonal-activities"], "related_systems": ["narrative-service", "activity-system", "travel-system"], "related_documents": [{"id": "canon-region-cis-almaty-2020-2029", "relation": "contextualizes"}, {"id": "github-issue-267", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/267", "relation": "processed_in", "processed_at": "2025-12-06T10:17:43+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/almaty/2020-2029/quest-005-shymbulak-ski.md", "visibility": "internal", "audience": ["lore", "live-ops", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий катания на Шымбулаке существовал в заметках и не был интегрирован в календарь спортивных событий Алматы.", "goal": "Структурировать горнолыжное событие как сезонную активность с маршрутами, атмосферой и наградами.", "essence": "Игрок поднимается на курорт Шымбулак, выбирает трассу и соревнуется в слаломе с видом на Алматы.", "key_points": ["Высокогорный курорт на высотах до 3 200 м с канатной дорогой.", "Несколько уровней трасс и мини-игра слалома.", "Финальный этап в кафе с социальным взаимодействием и баффами."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "ascend_cable_car", "text": "Подняться по канатной дороге от Медео к точке 2 200 м", "type": "travel", "target": "shymbulak_base", "count": 1, "optional": false}, {"id": "reach_summit", "text": "Дополнительный подъём на вершину 3 200 м и выбор трассы (зелёная/синяя/чёрная)", "type": "interact", "target": "shymbulak_summit", "count": 1, "optional": false}, {"id": "complete_slalom", "text": "Пройти слаломную мини-игру с учётом высотной разрежённости воздуха", "type": "interact", "target": "slalom_course", "count": 1, "optional": false}, {"id": "apres_ski", "text": "Вернуться в базовый лагерь и принять участие в apres-ski с NPC", "type": "interact", "target": "apres_ski_cafe", "count": 1, "optional": false}], "rewards": {"experience": 2000, "money": 0, "items": [{"item_id": "title_tianshan_skier", "quantity": 1}], "reputation": {}, "unlocks": ["shymbulak_extended_events"]}, "branches": []}, "content": {"sections": [{"id": "location", "title": "Курорт и окружение", "body": "Шымбулак расположен в горах Заилийского Алатау, всего в двадцати минутах от города. Канатная дорога соединяет\\nМедео с верхними станциями, открывая панораму Алматы. Семимесячный снежный сезон и олимпийская инфраструктура создают\\nощущение профессионального курорта.\\n", "mechanics_links": [], "assets": []}, {"id": "flow", "title": "Ход события", "body": "1. Подъём по канатной дороге от Медео к точке 2 200 м.\\n2. Дополнительный подъём на вершину 3 200 м и выбор трассы (зелёная/синяя/чёрная).\\n3. Прохождение слаломной мини-игры с учётом высотной разрежённости воздуха.\\n4. Возвращение в базовый лагерь и участие в apres-ski с NPC, обмен спортивными историями и баффами.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики и проверки", "body": "- **Слалом:** мини-игра на время с подбором оптимальной траектории.\\n- **Высокогорье:** модификаторы выносливости и дыхания.\\n- **Социальный блок:** обмен сувенирами и спортивными рейтингами в кафе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP и спортивный показатель +15.\\n- Титул «Горнолыжник Тянь-Шаня» и уникальные нашивки курорта.\\n- Доступ к расширенной версии мероприятия в поздних сезонах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Шымбулак", "definition": "Горнолыжный курорт в горах Заилийского Алатау недалеко от Алматы."}, {"term": "Apres-ski", "definition": "Традиция отдыха и общения после лыжных спусков."}], "references": [{"title": "Shymbulak Mountain Resort", "link": "https://shymbulak.com/en"}], "decisions": []}, "implementation": {"github_issue": 267, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 267. Детализирован контент согласно архитектуре квестов."}, {"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия события «Лыжи Шымбулак» в knowledge-entry и уточнение этапов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

