INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-baku-gobustan', 'Баку 2020-2029 — «Петроглифы Гобустана»', 'Игрок отправляется из Баку в Гобустан, изучает музей, восходит к древним рисункам и исследует уникальный ландшафт.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_to_gobustan", "text": "Добраться из Баку в заповедник Гобустан", "type": "interact", "target": "gobustan_travel", "count": 1, "optional": false}, {"id": "visit_museum", "text": "Посетить интерактивный музей с VR-экспозициями жизни охотников", "type": "interact", "target": "gobustan_museum_vr", "count": 1, "optional": false}, {"id": "explore_petroglyphs", "text": "Исследовать скальный массив и документировать петроглифы", "type": "interact", "target": "petroglyphs_documentation", "count": 1, "optional": false}, {"id": "visit_mud_volcanoes", "text": "Посетить грязевые вулканы и собрать образцы", "type": "interact", "target": "mud_volcanoes_collection", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "money": 0, "reputation": {"archaeology": 15}, "items": [], "unlocks": {"achievements": ["Археолог Гобустана"], "flags": ["gobustan_explored"]}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-baku-gobustan", "title": "Баку 2020-2029 — «Петроглифы Гобустана»", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["baku", "archaeology", "unesco"], "topics": ["exploration", "history"], "related_systems": ["narrative-service", "exploration-system", "education-system"], "related_documents": [{"id": "canon-region-cis-baku-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/baku/2020-2029/quest-009-gobustan-petroglyphs.md", "visibility": "internal", "audience": ["lore", "narrative", "exploration"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Путешествие в Гобустан описывалось поверхностно и не связывалось с образовательными механиками и уникальными объектами парка.", "goal": "Структурировать археологический квест с VR-музеем, маршрутом по петроглифам и бонусом грязевых вулканов.", "essence": "Игрок отправляется из Баку в Гобустан, изучает музей, восходит к древним рисункам и исследует уникальный ландшафт.", "key_points": ["Более 6 000 петроглифов и статус наследия ЮНЕСКО.", "VR-реконструкции и исследование на местности.", "Награды повышают параметр «Археология» и открывают историю Азербайджана."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_to_gobustan", "text": "Добраться из Баку в заповедник Гобустан", "type": "interact", "target": "gobustan_travel", "count": 1, "optional": false}, {"id": "visit_museum", "text": "Посетить интерактивный музей с VR-экспозициями жизни охотников", "type": "interact", "target": "gobustan_museum_vr", "count": 1, "optional": false}, {"id": "explore_petroglyphs", "text": "Исследовать скальный массив и документировать петроглифы", "type": "interact", "target": "petroglyphs_documentation", "count": 1, "optional": false}, {"id": "visit_mud_volcanoes", "text": "Посетить грязевые вулканы и собрать образцы", "type": "interact", "target": "mud_volcanoes_collection", "count": 1, "optional": false}], "rewards": {"experience": 2500, "money": 0, "reputation": {"archaeology": 15}, "items": [], "unlocks": {"achievements": ["Археолог Гобустана"], "flags": ["gobustan_explored"]}}, "branches": []}, "content": {"sections": [{"id": "museum", "title": "Музей и подготовка", "body": "Игрок прибывает в интерактивный музей Гобустана, где VR-экспозиции демонстрируют жизнь охотников, танцы и обряды.\\nКуратор объясняет правила посещения и уязвимость древних рисунков.\\n", "mechanics_links": [], "assets": []}, {"id": "trail", "title": "Тропа петроглифов", "body": "Посещение скального массива с гидами. Игрок документирует сцены охоты, танцевальные хороводы и изображения животных,\\nиспользуя сканер для архива и взаимодействие с timeline.\\n", "mechanics_links": [], "assets": []}, {"id": "mud_volcanoes", "title": "Грязевые вулканы", "body": "В завершение реализуется дополнительная активность — путешествие к грязевым вулканам. Игрок собирает образцы и получает\\nбонус к геологической ветке.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 500 XP, +15 к параметру «Археология» и ачивка «Археолог Гобустана».\\n- Репутация среди научных фракций и доступ к дополнительным исследованиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Гобустан", "definition": "Заповедник в Азербайджане с древними петроглифами и грязевыми вулканами, включённый в список ЮНЕСКО."}], "references": [{"title": "Gobustan Rock Art Cultural Landscape", "link": "https://whc.unesco.org/en/list/1076/"}], "decisions": []}, "implementation": {"github_issue": 906, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 906."}, {"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Петроглифы Гобустана» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLIC