-- Quest: canon-quest-washington-dc-2020-2029-lincoln-memorial
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-lincoln-memorial', 'Вашингтон 2020-2029 — «Мемориал Линкольна»', 'Игрок поднимается по ступеням, изучает надписи, наблюдает вид на National Mall и переосмысливает наследие Линкольна.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-lincoln-memorial", "title": "Вашингтон 2020-2029 — «Мемориал Линкольна»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "quest", "history"], "topics": ["city_quests", "memorials"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "tourism-service", "education-service"], "related_documents": [{"id": "github-issue-1030", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1030", "relation": "migrated_to", "migrated_at": "2025-11-23T02:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-003-lincoln-memorial.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Вашингтона не раскрывала символизм Мемориала Линкольна и его роль в движении за гражданские права.", "goal": "Показать архитектуру мемориала, речи Линкольна и Мартина Лютера Кинга, а также значимость Reflecting Pool.", "essence": "Игрок поднимается по ступеням, изучает надписи, наблюдает вид на National Mall и переосмысливает наследие Линкольна.", "key_points": ["Мемориал выполнен в форме древнегреческого храма и содержит статую высотой шесть метров.", "На ступенях проходила речь «I Have a Dream», что закрепило место в истории гражданских прав.", "Reflecting Pool создаёт визуальную ось между мемориалом и Вашингтонским монументом."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приводит игрока к западной части National Mall. Необходимо пройти маршрут паломника, изучая цитаты Линкольна и\\nчувствуя связь с событиями XX века.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Осмотреть колоннаду и архитектуру мемориала снаружи.\\n2. Подняться по ступеням и детально изучить статую Авраама Линкольна.\\n3. Прочитать выгравированные тексты Gettysburg Address и Second Inaugural Address.\\n4. Построить реконструкцию события 28 августа 1963 года и речи Мартина Лютера Кинга.\\n5. Завершить маршрут у Reflecting Pool, сделав панорамные заметки National Mall.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "- Мемориал открыт в 1922 году и с тех пор стал местом общественных акций.\\n- Статуя Линкольна высотой более шести метров создана скульптором Даниелом Честером Френчем.\\n- На ступенях имеется маркер, отмечающий место выступления Мартина Лютера Кинга.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 500 XP.\\n- Атрибут «История» +20.\\n- Достижение «Паломник Линкольна».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Исторический туризм и реконструкция ключевых событий.\\n- Социальные взаимодействия с активистами и экскурсоводами.\\n- Медиа-сбор, включая аудио и визуальные материалы.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает историческую линию Вашингтона и ведёт к сюжетам о гражданских правах и президентских мемориалах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1030, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Мемориал Линкольна» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

