INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-washington-2029-smithsonian-museums', 'Вашингтон 2020-2029 — Смитсоновские музеи', 'Игрок планирует маршрут по музеям, изучает знаковые экспонаты и формирует образовательные рекомендации.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-washington-2029-smithsonian-museums", "title": "Вашингтон 2020-2029 — Смитсоновские музеи", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T02:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "museums", "culture"], "topics": ["education", "public_access"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "education-service", "tourism-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1031", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1031", "relation": "migrated_to", "migrated_at": "2025-11-23T02:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-004-smithsonian-museums.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурная линия Вашингтона не раскрывала масштаб Смитсоновских музеев и их влияния на образование.", "goal": "Показать бесплатный доступ к коллекциям, охватить ключевые экспозиции и подчеркнуть значение National Mall.", "essence": "Игрок планирует маршрут по музеям, изучает знаковые экспонаты и формирует образовательные рекомендации.", "key_points": ["Смитсоновский институт включает 19 музеев и галерей с бесплатным входом.", "Самые посещаемые экспозиции — Air and Space, Natural History, American History, Африкано-американский музей.", "Национальный мол выступает «чердаком нации», где хранятся артефакты США."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест сопровождает игрока по Смитсоновским музеям, раскрывая образовательный потенциал и роль института в сохранении\\nкультурного наследия Соединённых Штатов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Спланировать маршрут по Национальному молу и распределить время между музеями.\\n2. Посетить National Air and Space Museum и изучить экспонаты программы «Аполлон».\\n3. Посетить National Museum of Natural History с динозаврами и алмазом Hope.\\n4. Исследовать National Museum of American History и African American History and Culture.\\n5. Составить отчёт о лучших экспозициях и рекомендованных образовательных программах.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Смитсоновский институт основан в XIX веке и содержит коллекции от авиации до искусства. Музеи расположены вдоль\\nNational Mall, что делает их доступными для жителей и туристов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP\\n- Атрибут «Культура»: +30\\n- Ачивка: «Смитсоновский учёный»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует образовательные и культурные механики, открывая дополнительные события музеев и бонусы к знаниям.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с культурной линией Вашингтона, заданиями об образовании и миссиями National Mall.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1031, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Смитсоновские музеи» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

