INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-almaty-big-lake', 'Алматы 2020-2029 — «Большое Алматинское озеро»', 'Игрок преодолевает путь к бирюзовому озеру на высоте 2 511 м, исследует обсерваторию и фиксирует виды.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_mountains", "text": "Отправиться из Алматы на 28 км к горной гряде, при необходимости пройти треккинг", "type": "travel", "target": "mountain_path", "count": 1, "optional": false}, {"id": "reach_lake", "text": "Добраться до бирюзового озера на высоте 2 511 м", "type": "travel", "target": "big_almaty_lake", "count": 1, "optional": false}, {"id": "photograph_lake", "text": "Активировать фото-режим, собрать природные артефакты и изучить легенды о запрете купания", "type": "interact", "target": "lake_photography", "count": 1, "optional": false}, {"id": "visit_observatory", "text": "Посетить астрономическую обсерваторию и получить данные для научных квестов", "type": "interact", "target": "observatory", "count": 1, "optional": true}]'::jsonb, '{"experience": 2000, "money": 0, "items": [{"item_id": "achievement_lake_guardian", "quantity": 1}], "reputation": {}, "unlocks": ["mountain_source_protection_questline"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-almaty-big-lake", "title": "Алматы 2020-2029 — «Большое Алматинское озеро»", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["almaty", "nature", "trekking"], "topics": ["exploration", "tourism"], "related_systems": ["narrative-service", "travel-system", "photo-system"], "related_documents": [{"id": "canon-region-cis-almaty-2020-2029", "relation": "contextualizes"}, {"id": "github-issue-267", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/267", "relation": "processed_in", "processed_at": "2025-12-06T10:17:43+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/almaty/2020-2029/quest-007-big-almaty-lake.md", "visibility": "internal", "audience": ["lore", "exploration", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Поход к Большому Алматинскому озеру не был представлен в структуре миссий и не связывался с системой природных баффов.", "goal": "Описать высокогорный треккинг с акцентом на природные испытания и культурные детали.", "essence": "Игрок преодолевает путь к бирюзовому озеру на высоте 2 511 м, исследует обсерваторию и фиксирует виды.", "key_points": ["Поездка в горы с возможными ограничениями проезда и треккингом.", "Фотосессия у бирюзовой воды и посещение обсерватории.", "Баффы от природной энергии и интеграция с travel-system."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_mountains", "text": "Отправиться из Алматы на 28 км к горной гряде, при необходимости пройти треккинг", "type": "travel", "target": "mountain_path", "count": 1, "optional": false}, {"id": "reach_lake", "text": "Добраться до бирюзового озера на высоте 2 511 м", "type": "travel", "target": "big_almaty_lake", "count": 1, "optional": false}, {"id": "photograph_lake", "text": "Активировать фото-режим, собрать природные артефакты и изучить легенды о запрете купания", "type": "interact", "target": "lake_photography", "count": 1, "optional": false}, {"id": "visit_observatory", "text": "Посетить астрономическую обсерваторию и получить данные для научных квестов", "type": "interact", "target": "observatory", "count": 1, "optional": true}], "rewards": {"experience": 2000, "money": 0, "items": [{"item_id": "achievement_lake_guardian", "quantity": 1}], "reputation": {}, "unlocks": ["mountain_source_protection_questline"]}, "branches": []}, "content": {"sections": [{"id": "journey", "title": "Путь к озеру", "body": "Игрок отправляется из Алматы на 28 км к горной гряде. При закрытой дороге требуется треккинг по горным тропам.\\nРазреженный воздух требует подготовки и расхода выносливости. На маршруте встречаются точки отдыха и NPC-гиды.\\n", "mechanics_links": [], "assets": []}, {"id": "lake_experience", "title": "Впечатления у озера", "body": "Бирюзовая вода, окружённая снежными вершинами Пиков Советов, Озёрный и Турист, создаёт идеальную сцену.\\nИгрок активирует фото-режим, собирает природные артефакты и изучает легенды о запрете купания.\\n", "mechanics_links": [], "assets": []}, {"id": "observatory", "title": "Обсерватория", "body": "Поблизости расположена астрономическая обсерватория. Внутри доступна мини-экскурсия, позволяющая открыть события\\nс наблюдением ночного неба и получить данные для научных квестов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP и показатель «Природа» +15.\\n- Ачивка «Хранитель озера» и набор карточек видов.\\n- Доступ к скрытой сюжетной ветке о защите горных источников.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "БАО", "definition": "Большое Алматинское озеро, высокогорный водоём на высоте 2 511 м."}], "references": [{"title": "Big Almaty Lake Guide", "link": "https://www.advantour.com/kazakhstan/almaty/big-almaty-lake.htm"}], "decisions": []}, "implementation": {"github_issue": 267, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 267. Детализирован контент согласно архитектуре квестов."}, {"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Большое Алматинское озеро» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

