INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-maidan-legacy', 'Киев 2020-2029 — «Наследие Майдана»', 'Игрок встречает участников революций на Майдане, изучает память «Небесной Сотни» и решает, поддерживать ли новый протест.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-maidan-legacy", "title": "Киев 2020-2029 — «Наследие Майдана»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "maidan", "revolution"], "topics": ["political-heritage", "civic-movement"], "related_systems": ["narrative-service", "diplomacy-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-001-maidan-legacy.md", "visibility": "internal", "audience": ["lore", "narrative", "political"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Набросок квеста не фиксировал структуру посещения Майдана и влияние выбора игрока на политические линии Киева.", "goal": "Оформить сценарий исследовательского визита на Майдан Незалежности с ветвлением решений и репутационными эффектами.", "essence": "Игрок встречает участников революций на Майдане, изучает память «Небесной Сотни» и решает, поддерживать ли новый протест.", "key_points": ["Мемориалы и хроника революций 2004 и 2014 годов.", "Диалоги с ветеранами и волонтёрами.", "Выборы влияют на политическую репутацию и доступ к последующим сюжетам."]}, "content": {"sections": [{"id": "memorial", "title": "Мемориал и хроника", "body": "Игрок прибывает на площадь, взаимодействует с цифровыми стендами и архивами протеста, слушает истории «Небесной Сотни»\\nи получает задания на сбор воспоминаний. Колонна Независимости, флаги и цветы формируют эмоциональный фон.\\n", "mechanics_links": [], "assets": []}, {"id": "veterans", "title": "Встреча с ветеранами", "body": "Диалоговая сцена с участниками революций. Игрок фиксирует их рассказы, сопоставляет перспективы 2004 и 2014 годов и\\nформирует собственное понимание свободы и ответственности.\\n", "mechanics_links": [], "assets": []}, {"id": "choice", "title": "Решение о будущем", "body": "- Поддержать новый протест, увеличив репутацию «Свобода» и усилив риск конфликта.\\n- Сохранить статус-кво, укрепив отношения с городскими властями и инвесторами.\\n- Инициировать компромиссный стол, открывая дипломатические цепочки.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Последствия", "body": "Итог фиксируется в политической линии Киева, меняет события будущих квестов и добавляет новости в мировую ленту.\\nИгрок получает 2 000 XP и +10 к репутации свободы при активной поддержке Майдана.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Небесная Сотня", "definition": "Граждане Украины, погибшие во время событий Евромайдана в 2014 году."}], "references": [{"title": "Euromaidan Overview", "link": "https://www.britannica.com/event/Euromaidan"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Наследие Майдана» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

