-- Quest: quest-washington-2029-washington-monument
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-washington-2029-washington-monument', 'Вашингтон 2020-2029 — Монумент Вашингтона', 'Игрок получает билет, поднимается на вершину монумента и изучает историю строительства.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-washington-2029-washington-monument", "title": "Вашингтон 2020-2029 — Монумент Вашингтона", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-23T02:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "architecture", "tourism"], "topics": ["national_symbols", "urban_landmarks"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "tourism-service", "education-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1032", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1032", "relation": "migrated_to", "migrated_at": "2025-11-23T02:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-005-washington-monument.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Вашингтона не уделяла должного внимания символу первого президента и панораме National Mall.", "goal": "Познакомить игрока с историей строительства обелиска и показать обзор столицы с высоты 169 м.", "essence": "Игрок получает билет, поднимается на вершину монумента и изучает историю строительства.", "key_points": ["Монумент — самый высокий обелиск в мире, строился с перерывами 40 лет.", "Видимая линия раздела камня отражает остановку строительства в XIX веке.", "Лифт поднимает посетителей на смотровую площадку за 70 секунд."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока к Монументу Вашингтона, раскрывая символику обелиска и значение обзора National Mall для\\nпонимания столицы США.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить бесплатный билет и подготовиться к подъёму.\\n2. Изучить внешний вид обелиска и отметить смену оттенка камня.\\n3. Подняться на вершину, зафиксировать панораму Национального мола и прилегающих памятников.\\n4. Посетить экспозицию о строительстве и реконструкции монумента.\\n5. Составить отчёт о визуальных ориентирах для туристических маршрутов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Монумент возвышается на 555 футов (169 м) и посвящён Джорджу Вашингтону. Различие в цвете облицовки отражает разные\\nэтапы строительства с 1848 по 1888 год.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 000 XP\\n- Атрибут «История»: +15\\n- Ачивка: «Покоритель обелиска»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест использует туристические и исторические механики, открывая бонусы к ориентации в столице и культурным сюжетам.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с исторической линией Вашингтона, заданиями о Национальном моле и сюжетами об отцах-основателях.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1032, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Монумент Вашингтона» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

